import{_ as i,C as o,c as s,o as n,aM as a,G as r}from"./chunks/framework.DAyGILei.js";const h=JSON.parse('{"title":"Data space orders","description":"HUNT Cloud service desk orders for data spaces.","frontmatter":{"title":"Data space orders","category":"Service desk","permalink":"/administer-science/service-desk/data-space-orders","description":"HUNT Cloud service desk orders for data spaces.","outline":2},"headers":[],"relativePath":"administer-science/service-desk/data-space-orders.md","filePath":"administer-science/service-desk/data-space-orders.md"}'),l={name:"administer-science/service-desk/data-space-orders.md"};function d(c,e,p,u,m,g){const t=o("SDButton");return n(),s("div",null,[e[0]||(e[0]=a('<h1 id="service-desk-orders-for-data-spaces" tabindex="-1">Service desk orders for data spaces <a class="header-anchor" href="#service-desk-orders-for-data-spaces" aria-label="Permalink to &quot;Service desk orders for data spaces&quot;">​</a></h1><p><strong>This page list predefined service orders for data space management. To place an order, click the blue button and send us a pre-filled email.</strong></p><nav class="table-of-contents"><ul><li><a href="#lab-management">Lab management</a><ul><li><a href="#new-lab">New lab</a></li><li><a href="#delete-lab">Delete lab</a></li><li><a href="#transfer-lab-between-data-spaces">Transfer lab between data spaces</a></li></ul></li><li><a href="#transfers">Transfers</a><ul><li><a href="#external-import-kista">External import kista</a></li><li><a href="#external-export-kista">External export kista</a></li><li><a href="#network-opening">Network opening</a></li></ul></li><li><a href="#data-space-management">Data space management</a><ul><li><a href="#update-data-space-administrator-roles">Update data space administrator roles</a></li><li><a href="#update-lab-leader-role">Update lab leader role</a></li><li><a href="#new-invoice-profile">New invoice profile</a></li><li><a href="#update-existing-invoice-profile">Update existing invoice profile</a></li></ul></li><li><a href="#tingweek">Tingweek</a><ul><li><a href="#deregister-to-future-tingweek-invitations">Deregister to future Tingweek invitations</a></li></ul></li><li><a href="#general-request">General request</a></li></ul></nav><div class="tip custom-block"><p class="custom-block-title">Save time</p><p>It saves you time to send us the request from your <strong>organizational email</strong>. We use this for identification. Requests from private emails need additional verification that takes longer.</p></div><p>Send us a regular <a href="/contact">email</a> with your request if you can&#39;t find what you are looking for on the this page.</p><h2 id="lab-management" tabindex="-1">Lab management <a class="header-anchor" href="#lab-management" aria-label="Permalink to &quot;Lab management&quot;">​</a></h2><h3 id="new-lab" tabindex="-1">New lab <a class="header-anchor" href="#new-lab" aria-label="Permalink to &quot;New lab&quot;">​</a></h3><p>Click the button below to order a new lab under your existing data space.</p>',8)),r(t,{form:"request_new_lab"}),e[1]||(e[1]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space compliance administrators in active data spaces, and lab leader for new labs.</li><li><strong>Required attachment</strong>: <a href="/administer-science/agreements/downloads#lab-order">A signed lab order</a> and information on storage volume sizes and machine type for the home machine (see below).</li><li><strong>Expected delivery time:</strong> One week.</li><li><strong>Next step</strong>: <a href="/administer-science/service-desk/lab-orders.html#add-a-new-lab-user">Add your first lab users</a>.</li><li><strong>Cost:</strong> One lab subscription as specified in the <a href="/administer-science/services/specifications">Services specifications</a> and <a href="/administer-science/prices/pricelist">Price list</a>.</li></ul><div class="warning custom-block"><p class="custom-block-title">Next: Add your first lab user(s)</p><p>It&#39;s important that you order lab access for each scientist (lab user) that will work in your lab as soon as you send us the above Lab order. Without these, your scientists will not be able to access your new lab.</p><p><a href="/administer-science/service-desk/lab-orders#add-a-new-lab-user">Click on this link to get going</a>.</p></div><details class="details custom-block"><summary>More on the required resource specifications</summary><p>Your lab comes with one (1) machine with computational resources and attached storage by default. We call this your <strong><code>home</code></strong>-machine.</p><p><strong>Machine type</strong></p><p>For cost savings, we recommend that you start with the <strong><code>default.b2</code></strong> machine type. And if needed, upgrade to larger machine types when your scientists are well onboard in your new lab and ready to explore. You may specify <a href="/administer-science/services/machine-types">alternative machines types</a> in the order if you know you need other sizes right away.</p><p><strong>Storage volumes</strong></p><p>Your lab comes with three (3) storage volumes attached to your home machine. We have suggested default sizes on these volumes that you may want to change.</p><p><em>Volume types</em></p><p>i) the <strong><code>archive</code></strong> volume is tailored for data storage, such as your raw data and results; ii) the <strong><code>work</code></strong> volume is tailored for your data in your day-to-day activities that your want to preserve, such as code files; and iii) the <strong><code>scratch</code></strong> volume is tailored for temporary analytical files, such typically generated in intermediate steps of your analysis. Note that the <strong><code>scratch</code></strong> volume do -not- include our restore service (backup).</p><p><em>Volume sizes</em></p><p>The minimum storage allocation of the three volumes is 1000 Gigabytes (1 Terabyte). We have suggested a default allocation that you may update if you like:</p><p>ARCHIVE = 400 WORK = 300 SCRATCH = 300</p><p>The above numbers are in Gigabytes (1000 Gigabytes = 1 Terabyte). To illustrate, if you want your archive volume to be 2 Terabyte, specify <strong><code>ARCHIVE = 2000</code></strong> in your order.</p><p><strong>More information</strong></p><p>See the &quot;Lab&quot; section in our <a href="/administer-science/services/specifications">Services specifications</a> for more information on the lab service and default configuration. <a href="/contact">Contact us</a> if you need guidance on the size selection.</p></details><h3 id="delete-lab" tabindex="-1">Delete lab <a class="header-anchor" href="#delete-lab" aria-label="Permalink to &quot;Delete lab&quot;">​</a></h3><p>Click the button below to order a lab deletion of an active lab under your data space.</p>',5)),r(t,{form:"request_lab_deletion"}),e[2]||(e[2]=a('<ul><li><strong>Who can order:</strong> Data space leaders and Lab leaders in collaboration.</li><li><strong>Required attachment</strong>: <a href="/administer-science/agreements/downloads#lab-deletion-order">A signed lab deletion order</a>.</li><li><strong>Expected response time:</strong> Weeks.</li><li><strong>Expected delivery:</strong> Countersignature from HUNT Cloud on your lab deletion order confirming that lab data, including online and offline duplicate copies, are deleted.</li><li><strong>Cost:</strong> Included in your lab subscription.</li></ul><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>Once your lab data is deleted it can not be recreated. You must ensure that you have exported all data that you intend to preserve from the lab -before- you order the lab deletion. <a href="/contact">Contact us</a> if you need guidance with this work.</p></div><h3 id="transfer-lab-between-data-spaces" tabindex="-1">Transfer lab between data spaces <a class="header-anchor" href="#transfer-lab-between-data-spaces" aria-label="Permalink to &quot;Transfer lab between data spaces&quot;">​</a></h3><p>You can transfer a lab between two data spaces by filling form below.</p>',4)),r(t,{form:"request_lab_transfer"}),e[3]||(e[3]=a('<ul><li><strong>Who can order:</strong> Data space leaders and compliance administrators</li><li><strong>Required attachment</strong>: A <a href="/administer-science/agreements/downloads#lab-transfer-order">signed lab transfer agreement</a>.</li><li><strong>Optional attachment</strong>: A <a href="/administer-science/agreements/downloads#invoice-profile">signed invoice agreemnent</a></li><li><strong>Expected response time:</strong> Days.</li><li><strong>Expected delivery:</strong> A lab transferred to a new data space with updated inovice profile.</li><li><strong>Cost:</strong> Included in your lab subscription.</li></ul><h2 id="transfers" tabindex="-1">Transfers <a class="header-anchor" href="#transfers" aria-label="Permalink to &quot;Transfers&quot;">​</a></h2><p>Data transfers in and out of HUNT Cloud needs authorization from Data space leaders or Data space compliance administrators.</p><div class="tip custom-block"><p class="custom-block-title">Internal transfers and import</p><p>Data transfers between labs inside HUNT Cloud can be requested from our <a href="/administer-science/service-desk/lab-orders">lab order service desk</a>.</p></div><h3 id="external-import-kista" tabindex="-1">External import kista <a class="header-anchor" href="#external-import-kista" aria-label="Permalink to &quot;External import kista&quot;">​</a></h3><p>The <a href="/do-science/faq/external-transfer#faq-on-external-data-transfer">external import kista</a> allows external parties outside HUNT Cloud to transfer data directly to a lab inside HUNT Cloud in a compliant manner.</p>',6)),r(t,{form:"request_external_import_kista"}),e[4]||(e[4]=a('<ul><li><strong>Who can order:</strong> Data space leaders or Data space compliance administrators.</li><li><strong>Required attachments</strong>: (1) <a href="/administer-science/agreements/downloads#external-kista-import-order">A signed external kista import order</a>, and (2) one <a href="/do-science/data-transfers/external-kista#ssh-key-pair">SSH public key</a> from the external uploader.</li><li><strong>Expected response time:</strong> Days.</li><li><strong>Expected delivery:</strong> Kista access information sent to the lab user that will import the data.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><div class="warning custom-block"><p class="custom-block-title">External dependency</p><p>Note that time to successful transfer depend on access to SFTP software and potential firewall adjustments for the external-party that will download data.</p></div><h3 id="external-export-kista" tabindex="-1">External export kista <a class="header-anchor" href="#external-export-kista" aria-label="Permalink to &quot;External export kista&quot;">​</a></h3><p>The <a href="/do-science/faq/external-transfer#faq-on-external-data-transfer">external export kista</a> allows labs to make data available for external parties outside HUNT Cloud in a compliant manner.</p>',4)),r(t,{form:"request_external_export_kista"}),e[5]||(e[5]=a('<ul><li><strong>Who can order:</strong> Data space leaders or Data space compliance administrators.</li><li><strong>Required attachments</strong>: (1) <a href="/administer-science/agreements/downloads#external-kista-export-order">A signed external kista export order</a>, and (2) one <a href="/do-science/data-transfers/external-kista#ssh-key-pair">SSH public key</a> from the external downloader.</li><li><strong>Expected response time:</strong> Days.</li><li><strong>Expected delivery:</strong> Access information sent to the lab user that will make data available for export.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><div class="warning custom-block"><p class="custom-block-title">External dependency</p><p>Note that time to successful transfer depends on access to SFTP software and potential firewall adjustments for the external-party that will upload data.</p></div><h3 id="network-opening" tabindex="-1">Network opening <a class="header-anchor" href="#network-opening" aria-label="Permalink to &quot;Network opening&quot;">​</a></h3><p><a href="/do-science/faq/external-transfer#direct-external-transfers">Network opening orders</a> allows data transfers directly to and from parties outside HUNT Cloud.</p>',4)),r(t,{form:"request_network_opening"}),e[6]||(e[6]=a('<ul><li><strong>Who can order:</strong> Data space leaders or Data space compliance administrators.</li><li><strong>Required information</strong>: IPv4 address and port number for the external party.</li><li><strong>Required attachments</strong>: <a href="/administer-science/agreements/downloads#network-opening-order">A signed network opening order</a>.</li><li><strong>Expected response time:</strong> Days.</li><li><strong>Expected delivery:</strong> Network opening communicated in your Slack lab channel.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h2 id="data-space-management" tabindex="-1">Data space management <a class="header-anchor" href="#data-space-management" aria-label="Permalink to &quot;Data space management&quot;">​</a></h2><h3 id="update-data-space-administrator-roles" tabindex="-1">Update data space administrator roles <a class="header-anchor" href="#update-data-space-administrator-roles" aria-label="Permalink to &quot;Update data space administrator roles&quot;">​</a></h3><p>Click the button below to update your <a href="/administer-science/roles">data space administrator</a> under your existing data space.</p>',4)),r(t,{form:"update_data_space_administrator_role"}),e[7]||(e[7]=a('<ul><li><strong>Who can order:</strong> Data space leaders or Data space compliance administrators in active data spaces.</li><li><strong>Required attachment</strong>: None.</li><li><strong>Expected delivery time:</strong> Days.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h3 id="update-lab-leader-role" tabindex="-1">Update lab leader role <a class="header-anchor" href="#update-lab-leader-role" aria-label="Permalink to &quot;Update lab leader role&quot;">​</a></h3><p>Click the button below to update lab leader roles for labs attached to your data space.</p>',3)),r(t,{form:"request_lab_leader_role"}),e[8]||(e[8]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space compliance officers.</li><li><strong>Required attachment</strong>: A new <a href="/administer-science/agreements/downloads#lab-order">signed lab order</a>.</li><li><strong>Expected delivery time:</strong> Days.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h3 id="new-invoice-profile" tabindex="-1">New invoice profile <a class="header-anchor" href="#new-invoice-profile" aria-label="Permalink to &quot;New invoice profile&quot;">​</a></h3><p>Click the button below to add a new invoice profile to your data space. Use this is you want a new lab to be invoiced to a specific account that you control, or if you want to separate one of your existing labs into a separate account.</p>',3)),r(t,{form:"request_new_invoice_profile"}),e[9]||(e[9]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space financial officers.</li><li><strong>Required attachment</strong>: A new <a href="/administer-science/agreements/downloads#invoice-profile">signed invoice profile</a>.</li><li><strong>Expected delivery time:</strong> Days.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h3 id="update-existing-invoice-profile" tabindex="-1">Update existing invoice profile <a class="header-anchor" href="#update-existing-invoice-profile" aria-label="Permalink to &quot;Update existing invoice profile&quot;">​</a></h3><p>Click the button below to update an existing invoice profile to your data space. Use this is you want to update your account information, update your financial contact etc.</p>',3)),r(t,{form:"request_update_of_existing_invoice"}),e[10]||(e[10]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space financial officers.</li><li><strong>Required attachment</strong>: A new <a href="/administer-science/agreements/downloads#invoice-profile">signed invoice profile</a>.</li><li><strong>Expected delivery time:</strong> Days.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h2 id="tingweek" tabindex="-1">Tingweek <a class="header-anchor" href="#tingweek" aria-label="Permalink to &quot;Tingweek&quot;">​</a></h2><h3 id="deregister-to-future-tingweek-invitations" tabindex="-1">Deregister to future Tingweek invitations <a class="header-anchor" href="#deregister-to-future-tingweek-invitations" aria-label="Permalink to &quot;Deregister to future Tingweek invitations&quot;">​</a></h3><p>All active HUNT Cloud users will be invited to individual Tings three times per year during our Tingweeks. Participation is voluntary and you can deregister to receive future invitations here.</p>',4)),r(t,{form:"deregister_tingweek_invitations"}),e[11]||(e[11]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space administrators.</li><li><strong>Expected delivery time:</strong> Days.</li><li><strong>Expected response time:</strong> Confirmation of deregistration.</li><li><strong>Cost:</strong> Included in your data space subscription.</li></ul><h2 id="general-request" tabindex="-1">General request <a class="header-anchor" href="#general-request" aria-label="Permalink to &quot;General request&quot;">​</a></h2><p>Don&#39;t find what you are looking for on the predefined orders above? Don&#39;t you worry. Send us a general service request.</p>',3)),r(t,{form:"general_dataspace_request"}),e[12]||(e[12]=a('<ul><li><strong>Who can order:</strong> Data space leaders and data space coordinators.</li><li><strong>Expected response time:</strong> Days.</li></ul><div class="warning custom-block"><p class="custom-block-title">General responses are slower</p><p>Note that a general request has slower response time than a predefined order. If you don&#39;t find what you are looking for here, it might be good to invest some time investigating options in our <a href="/do-science/service-desk/">do science</a> and <a href="/govern-science/service-desk/">govern science</a> service desk.</p></div><div class="tip custom-block"><p class="custom-block-title">Not an active user?</p><p><a href="/contact">Contact us</a> on email for general questions and information on our scientific services and activities.</p></div>',3))])}const b=i(l,[["render",d]]);export{h as __pageData,b as default};
