import{_ as s,a as i}from"./chunks/wb_cp_stopserver.Dt2HKZxn.js";import{_ as n,c as t,o,aM as r}from"./chunks/framework.DAyGILei.js";const l="/assets/wb_notebook_envs.BXU7DaR-.png",h="/assets/wb_notebook_kernel.ChnZWRKb.png",p="/assets/wb_notebook_conda_env.CDvMbR_4.png",c="/assets/MATLAB-manage-clusters.DjzNhP6L.png",d="/assets/MATLAB-numWorkers.DptmhxPV.png",e="/assets/workbench_terminal.Dm-icZAp.png",k="/assets/other_apps_terminal.Caa6iS0Y.png",C=JSON.parse('{"title":"FAQ","description":"Frequently asked questions about HUNT Workbench.","frontmatter":{"title":"FAQ","category":"HUNT Workbench","permalink":"/do-science/hunt-workbench/faq","outline":1,"description":"Frequently asked questions about HUNT Workbench."},"headers":[],"relativePath":"do-science/hunt-workbench/faq.md","filePath":"do-science/hunt-workbench/faq.md"}'),g={name:"do-science/hunt-workbench/faq.md"};function u(m,a,y,b,F,v){return o(),t("div",null,[...a[0]||(a[0]=[r('<h1 id="hunt-workbench-faq" tabindex="-1">HUNT Workbench FAQ <a class="header-anchor" href="#hunt-workbench-faq" aria-label="Permalink to &quot;HUNT Workbench FAQ&quot;">​</a></h1><p><strong>This page contain frequently asked questions about Workbench. Help us expand this section by <a href="/contact">sending us questions and ideas</a> for new content.</strong></p><nav class="table-of-contents"><ul><li><a href="#storage">Storage</a><ul><li><a href="#where-is-my-data">Where is my data?</a></li><li><a href="#where-is-my-code-stored">Where is my code stored?</a></li></ul></li><li><a href="#reproducibility">Reproducibility</a><ul><li><a href="#can-i-create-my-own-conda-environment-for-jupyter">Can I create my own Conda environment for Jupyter?</a></li><li><a href="#how-can-i-start-a-notebook-with-my-new-conda-environment">How can I start a Notebook with my new Conda environment?</a></li><li><a href="#how-can-i-change-a-conda-environment-for-my-notebook">How can I change a Conda environment for my Notebook?</a></li><li><a href="#can-others-use-packages-i-have-installed">Can others use packages I have installed?</a></li><li><a href="#how-can-i-share-my-conda-environments-with-others">How can I share my Conda environments with others?</a></li><li><a href="#how-can-i-install-apt-packages">How can I install apt packages?</a></li></ul></li><li><a href="#management">Management</a><ul><li><a href="#how-can-i-restart-my-workbench-environment">How can I restart my workbench environment?</a></li><li><a href="#can-i-install-jupyter-extensions">Can I install Jupyter extensions?</a></li></ul></li><li><a href="#matlab">MATLAB</a><ul><li><a href="#are-there-any-limitations-to-matlab-in-hunt-workbench">Are there any limitations to MATLAB in HUNT Workbench?</a></li><li><a href="#how-do-i-report-issues-with-matlab">How do I report issues with MATLAB?</a></li><li><a href="#how-can-i-increase-the-number-of-parallel-workers">How can I increase the number of parallel workers?</a></li><li><a href="#can-i-install-new-toolboxes">Can I install new toolboxes</a></li></ul></li><li><a href="#rstudio">RStudio</a><ul><li><a href="#which-r-version-is-available-in-rstudio">Which R version is available in RStudio?</a></li><li><a href="#which-r-packages-are-preinstalled">Which R packages are preinstalled?</a></li><li><a href="#how-do-i-install-additional-r-packages">How do I install additional R packages?</a></li><li><a href="#how-do-i-install-r-packages-not-found-in-conda">How do I install R packages not found in Conda?</a></li><li><a href="#how-can-i-use-r-in-my-workbench-terminal">How can I use R in my Workbench Terminal?</a></li><li><a href="#how-can-i-recover-my-r-environment-in-case-of-broken-package-installation">How can I recover my R environment in case of broken package installation?</a></li><li><a href="#can-others-use-my-r-packages">Can others use my R packages?</a></li><li><a href="#how-do-i-reset-my-user-state-in-rstudio">How do I reset my user state in RStudio?</a></li></ul></li><li><a href="#visual-studio-code">Visual Studio Code</a><ul><li><a href="#pre-installed-extensions">Pre-installed extensions</a></li><li><a href="#can-i-install-my-own-extensions">Can I install my own extensions?</a></li></ul></li><li><a href="#terminal">Terminal</a><ul><li><a href="#can-i-use-a-terminal-from-my-workbench">Can I use a terminal from my Workbench?</a></li><li><a href="#when-do-i-need-to-use-a-terminal">When do I need to use a terminal?</a></li></ul></li></ul></nav><div class="tip custom-block"><p class="custom-block-title">Troubleshooting</p><p>Head over to our <a href="/do-science/hunt-workbench/troubleshooting">HUNT Workbench troubleshooting</a> section if something is not working as expected.</p></div><h2 id="storage" tabindex="-1">Storage <a class="header-anchor" href="#storage" aria-label="Permalink to &quot;Storage&quot;">​</a></h2><h3 id="where-is-my-data" tabindex="-1">Where is my data? <a class="header-anchor" href="#where-is-my-data" aria-label="Permalink to &quot;Where is my data?&quot;">​</a></h3><p>HUNT Workbench uses the same storages volumes as your home machine. This means that you can access all lab data, including your <strong><code>home</code></strong>-directory, using the same paths in your scripts, for example:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/mnt/archive/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&lt;my-data-folder&gt;</span></span></code></pre></div><h3 id="where-is-my-code-stored" tabindex="-1">Where is my code stored? <a class="header-anchor" href="#where-is-my-code-stored" aria-label="Permalink to &quot;Where is my code stored?&quot;">​</a></h3><p>Your Jupyter Notebooks and code files are stored in the following shared <strong><code>work</code></strong>-volume on your home machine:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/mnt/work/workbench</span></span></code></pre></div><h2 id="reproducibility" tabindex="-1">Reproducibility <a class="header-anchor" href="#reproducibility" aria-label="Permalink to &quot;Reproducibility&quot;">​</a></h2><h3 id="can-i-create-my-own-conda-environment-for-jupyter" tabindex="-1">Can I create my own Conda environment for Jupyter? <a class="header-anchor" href="#can-i-create-my-own-conda-environment-for-jupyter" aria-label="Permalink to &quot;Can I create my own Conda environment for Jupyter?&quot;">​</a></h3><p>Yes. This allows you to install packages and package versions of your choice.</p><p>Below is an example on how you create a Conda environments with <em>Python</em> and <em>IPython</em> kernel from your <a href="/do-science/hunt-workbench/faq#terminal">workbench terminal</a>::</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mamba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">nam</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">e</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;python~=3.10.*&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;ipykernel&#39;</span></span></code></pre></div><p>If you have older setup and missing mamba, you can use Conda command instead:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mamba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">nam</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">e</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;python~=3.10.*&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;ipykernel&#39;</span></span></code></pre></div><p>And here&#39;s an example on how to create a Conda environment with <em>R</em> and <em>R-IRkernel</em> kernel and commonly used packages:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mamba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">nam</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">e</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-base&gt;=4.0,&lt;5.0&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-irkernel&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-devtools&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-remotes&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-dplyr&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-tidyverse&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-haven&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;cmake&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;compilers&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;cmake&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;gmp&#39;</span></span></code></pre></div><p>You need to replace <strong><code>&lt;name&gt;</code></strong> with your own environment name in the example above, such as <strong><code>conda-test-environment</code></strong>.</p><h3 id="how-can-i-start-a-notebook-with-my-new-conda-environment" tabindex="-1">How can I start a Notebook with my new Conda environment? <a class="header-anchor" href="#how-can-i-start-a-notebook-with-my-new-conda-environment" aria-label="Permalink to &quot;How can I start a Notebook with my new Conda environment?&quot;">​</a></h3><p>You should see Notebooks with the <strong><code>conda env</code></strong>-tag. Here are two examples of such Notebooks that are connected to custom Conda environments.</p><p><img src="'+l+'" alt="wb_notebook_envs.png"></p><h3 id="how-can-i-change-a-conda-environment-for-my-notebook" tabindex="-1">How can I change a Conda environment for my Notebook? <a class="header-anchor" href="#how-can-i-change-a-conda-environment-for-my-notebook" aria-label="Permalink to &quot;How can I change a Conda environment for my Notebook?&quot;">​</a></h3><p>You can change the Conda environment for your Jupyter Notebook when it is open: Open your Jupyter Notebook of choice and switch the kernel in the top right corner of menu:</p><p><img src="'+h+'" alt="wb_notebook_kernel.png"></p><p>You will be able to select default environments including MATLAB kernel and custom environments which contain either <strong><code>ipykernel</code></strong> (Python) or <strong><code>r-irkernel</code></strong> (R 4.0):</p><p><img src="'+p+`" alt="wb_notebook_conda_env.png"></p><h3 id="can-others-use-packages-i-have-installed" tabindex="-1">Can others use packages I have installed? <a class="header-anchor" href="#can-others-use-packages-i-have-installed" aria-label="Permalink to &quot;Can others use packages I have installed?&quot;">​</a></h3><p>Not directly. The packages you install in your Workbench are usually not shared. The separation prevent others from breaking your packages, for example during upgrades. However, you may utilize the powers of Conda to export the definition of your environments so others can use restore function to be able to use the very same packages that you use. Learn how to manage environments on <a href="https://docs.conda.io/projects/conda/en/latest/user-guide/tasks/manage-environments.html" target="_blank" rel="noreferrer">docs.conda.io</a> or skip directly to <a href="https://docs.conda.io/projects/conda/en/latest/user-guide/tasks/manage-environments.html#sharing-an-environment" target="_blank" rel="noreferrer">sharing an environment guide</a>.</p><h3 id="how-can-i-share-my-conda-environments-with-others" tabindex="-1">How can I share my Conda environments with others? <a class="header-anchor" href="#how-can-i-share-my-conda-environments-with-others" aria-label="Permalink to &quot;How can I share my Conda environments with others?&quot;">​</a></h3><p>It might be handy to share your exact setup with your lab colleagues when you have perfected your workflows.</p><p>The packages that you have installed in your HUNT Workbench environment are usually not shared. The separation prevent others from breaking your packages and it allows you to explore packages without breaking the workflows of others.</p><p>So, to share environments you will need to export your environments definition to your colleagues so they can reuse them. Read more on how to <a href="https://docs.conda.io/projects/conda/en/latest/user-guide/tasks/manage-environments.html#sharing-an-environment" target="_blank" rel="noreferrer">share</a> and manage Code environments on <a href="https://docs.conda.io/projects/conda/en/latest/user-guide/tasks/manage-environments.html" target="_blank" rel="noreferrer">docs.conda.io</a>. Below is a principle guide run in your <a href="/do-science/hunt-workbench/faq#terminal">Workbench terminal</a>:</p><ol><li>In your Workbench:</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- export your active environment</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> env</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> export</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &gt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/test-environment.yml</span></span></code></pre></div><ol start="2"><li>In your lab colleague&#39;s Workbench (run by your lab-colleague):</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- create a new environment from the saved environment file</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conde</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> env</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -f</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/test-environment.yml</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- activate the environment (it will have the same name as your original environment)</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> activate</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">nam</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">e</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- verify the installation</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> env</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> list</span></span></code></pre></div><h3 id="how-can-i-install-apt-packages" tabindex="-1">How can I install apt packages? <a class="header-anchor" href="#how-can-i-install-apt-packages" aria-label="Permalink to &quot;How can I install apt packages?&quot;">​</a></h3><p>We recommend that instead of using apt, you install your packages into your environment using Conda since these will be installed on disk and such be persistent during restarts. Regular <strong><code>apt</code></strong> commands run in terminal will be removed during your next restart. Search <a href="https://anaconda.org/" target="_blank" rel="noreferrer">Conda repository</a> to identify your package name and get going with your installation.</p><p>Example with <a href="https://anaconda.org/bioconda/plink2" target="_blank" rel="noreferrer">plink2</a> package:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>mamba install -n default -c bioconda -y plink2</span></span>
<span class="line"><span>conda activate default</span></span>
<span class="line"><span>plink2 --help</span></span></code></pre></div><h2 id="management" tabindex="-1">Management <a class="header-anchor" href="#management" aria-label="Permalink to &quot;Management&quot;">​</a></h2><h3 id="how-can-i-restart-my-workbench-environment" tabindex="-1">How can I restart my workbench environment? <a class="header-anchor" href="#how-can-i-restart-my-workbench-environment" aria-label="Permalink to &quot;How can I restart my workbench environment?&quot;">​</a></h3><p>Sometimes you might want to start over with a fresh environment:</p><p>(1) Select control panel in top right corner:</p><p><img src="`+s+'" alt="wb_topbar_cp.png"></p><p>(2) Then in the control panel hit stop server:</p><p><img src="'+i+'" alt="wb_cp_stopserver.png"></p><p>You can either start the new instance by hitting the button or by logging in again.</p><h3 id="can-i-install-jupyter-extensions" tabindex="-1">Can I install Jupyter extensions? <a class="header-anchor" href="#can-i-install-jupyter-extensions" aria-label="Permalink to &quot;Can I install Jupyter extensions?&quot;">​</a></h3><p>No. Due to security reasons, we have disabled the Jupyter extension manager. <a href="/contact">Contact us</a> if you would like useful extensions to be added to the HUNT Workbench.</p><h2 id="matlab" tabindex="-1">MATLAB <a class="header-anchor" href="#matlab" aria-label="Permalink to &quot;MATLAB&quot;">​</a></h2><h3 id="are-there-any-limitations-to-matlab-in-hunt-workbench" tabindex="-1">Are there any limitations to MATLAB in HUNT Workbench? <a class="header-anchor" href="#are-there-any-limitations-to-matlab-in-hunt-workbench" aria-label="Permalink to &quot;Are there any limitations to MATLAB in HUNT Workbench?&quot;">​</a></h3><p>Yes, there are some limitations to the web-based version of MATLAB (<em>MATLAB Online</em>). Read more in the <a href="https://se.mathworks.com/products/matlab-online/limitations.html" target="_blank" rel="noreferrer">official documentation</a> from MathWorks.</p><h3 id="how-do-i-report-issues-with-matlab" tabindex="-1">How do I report issues with MATLAB? <a class="header-anchor" href="#how-do-i-report-issues-with-matlab" aria-label="Permalink to &quot;How do I report issues with MATLAB?&quot;">​</a></h3><p>Try to document the time when the issue occurred and save the error logs from your Status information window. Additionally, it would also help if you could take a screenshot of the error logs in the browser console. Usually you can open the console with the shortcuts: <strong><code>CTRL + SHIFT + J</code></strong> (MacOS: <strong><code>CMD + Option + J</code></strong>), although this may vary between browsers. Share the information with us on <a href="/contact">Slack or email</a>.</p><h3 id="how-can-i-increase-the-number-of-parallel-workers" tabindex="-1">How can I increase the number of parallel workers? <a class="header-anchor" href="#how-can-i-increase-the-number-of-parallel-workers" aria-label="Permalink to &quot;How can I increase the number of parallel workers?&quot;">​</a></h3><p>If you already have &quot;<em>Parallel Computing Toolbox</em>&quot; and cannot set the higher number parallel workers in the pool, select <strong><code>Parallel</code></strong> menu and click on <strong><code>Create and Manage Clusters</code></strong>:</p><p><img src="'+c+'" alt="MATLAB-manage-clusters.png"></p><p>Then, hit <strong><code>Edit</code></strong> and update the <strong><code>NumWorkers</code></strong> value (usually number of CPUs):</p><p><img src="'+d+'" alt="MATLAB-numWorkers.png"></p><h3 id="can-i-install-new-toolboxes" tabindex="-1">Can I install new toolboxes <a class="header-anchor" href="#can-i-install-new-toolboxes" aria-label="Permalink to &quot;Can I install new toolboxes&quot;">​</a></h3><p>We need to initiate such installation. <a href="/contact">Contact us</a> on email if you need new toolboxes in your MATLAB installation.</p><h2 id="rstudio" tabindex="-1">RStudio <a class="header-anchor" href="#rstudio" aria-label="Permalink to &quot;RStudio&quot;">​</a></h2><h3 id="which-r-version-is-available-in-rstudio" tabindex="-1">Which R version is available in RStudio? <a class="header-anchor" href="#which-r-version-is-available-in-rstudio" aria-label="Permalink to &quot;Which R version is available in RStudio?&quot;">​</a></h3><p>RStudio is configured to use packages installed in the Conda environment named <strong><code>r-base</code></strong>. You can identify your current <em>R version</em> packages in your <a href="/do-science/hunt-workbench/faq#terminal">Workbench Terminal</a> following these steps:</p><ol><li>Open your Workbench Terminal.</li></ol><p><img src="'+e+`" alt="workbench_terminal.png"></p><ol start="2"><li>List the packages in your r-base Conda environment.</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> list</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> |</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> grep</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span></span></code></pre></div><h3 id="which-r-packages-are-preinstalled" tabindex="-1">Which R packages are preinstalled? <a class="header-anchor" href="#which-r-packages-are-preinstalled" aria-label="Permalink to &quot;Which R packages are preinstalled?&quot;">​</a></h3><p>Currently, we install your <strong><code>r-base</code></strong> environment with the following Conda packages:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-haven</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-irkernel</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-devtools</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-dplyr</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-remotes</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">-</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-tidyverse</span></span></code></pre></div><p>If you have older setup and missing some of these packages you can follow the &quot;<em>How do I install additional R packages</em>&quot;-answer below to add them to your Conda environment.</p><h3 id="how-do-i-install-additional-r-packages" tabindex="-1">How do I install additional R packages? <a class="header-anchor" href="#how-do-i-install-additional-r-packages" aria-label="Permalink to &quot;How do I install additional R packages?&quot;">​</a></h3><p>If you want to install your own R packages, we recommend that you use <a href="/do-science/tools/analytical/conda">Conda</a> where ever possible:</p><ol><li>Open <a href="/do-science/hunt-workbench/faq#terminal">Workbench Terminal</a>:</li></ol><p><img src="`+e+`" alt="workbench_terminal.png"></p><ol start="2"><li>Activate r-base environment</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> activate</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span></span></code></pre></div><ol start="3"><li>Add packages to your <strong><code>r-base</code></strong> code environment:</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Principal example</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -c</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> conda-forge</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-&#39;&lt;package-name&gt;&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-&#39;&lt;another-package-name&gt;&#39;</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Practical example*</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mamba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -c</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> conda-forge</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-dplyr</span></span></code></pre></div><p>For example, the above example installs the <a href="https://anaconda.org/conda-forge/r-dplyr" target="_blank" rel="noreferrer">dplyr package</a>.</p><ol start="3"><li>When the installation is complete, open <em>RStudio</em> or <em>R</em> in your workbench and load your new package:</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Principal example</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">library(</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&lt;package-name&gt;)</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Practical example</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">library(dplyr</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)</span></span></code></pre></div><p>You can defer to alternative methods such as <code>install.packages()</code> when the package is not available in Conda (see below).</p><h3 id="how-do-i-install-r-packages-not-found-in-conda" tabindex="-1">How do I install R packages not found in Conda? <a class="header-anchor" href="#how-do-i-install-r-packages-not-found-in-conda" aria-label="Permalink to &quot;How do I install R packages not found in Conda?&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">Never install R / Bioconductor packages from inside RStudio</p><br></div><p>If you don&#39;t find a <em>R package</em> in Conda as described above, you may need to defer to running the <strong><code>install.packages()</code></strong> or <strong><code>BiocManager::install()</code></strong> commands inside <strong><code>R</code></strong>. We recommend to use <strong><code>R</code></strong> in your <a href="/do-science/hunt-workbench/faq#terminal">workbench terminal</a> for such installations (instead of installing directly in RStudio) to avoid broken environment states in RStudio.</p><p>Packages installed into your <strong><code>r-base</code></strong> environment through <strong>R</strong> in <a href="/do-science/hunt-workbench/faq#terminal">terminal</a> will be available in <strong>RStudio</strong>.</p><p>Make sure to activate the <code>r-base</code> environment before running <strong>R</strong> command.</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> activate</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span></span></code></pre></div><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">R</span></span></code></pre></div><h3 id="how-can-i-use-r-in-my-workbench-terminal" tabindex="-1">How can I use R in my Workbench Terminal? <a class="header-anchor" href="#how-can-i-use-r-in-my-workbench-terminal" aria-label="Permalink to &quot;How can I use R in my Workbench Terminal?&quot;">​</a></h3><p>First, open your <a href="/do-science/hunt-workbench/faq#terminal">Workbench Terminal</a>.</p><p>Start by activating <code>r-base</code> environment:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> activate</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span></span></code></pre></div><p>Then run <strong>R</strong> inside your <code>r-base</code> environment:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">R</span></span></code></pre></div><p>Enjoy!</p><h3 id="how-can-i-recover-my-r-environment-in-case-of-broken-package-installation" tabindex="-1">How can I recover my R environment in case of broken package installation? <a class="header-anchor" href="#how-can-i-recover-my-r-environment-in-case-of-broken-package-installation" aria-label="Permalink to &quot;How can I recover my R environment in case of broken package installation?&quot;">​</a></h3><p>Sometimes you may get errors about the a library not being found (e.g. <strong><code>GLIBCXX</code></strong>, <strong><code>OPENSSL</code></strong>) and/or essential R packages are not working. This happens typically after installation of custom package. In such cases, you might consider to recreate your <strong><code>r-base</code></strong> environment from scratch:</p><ol><li>Open your <a href="/do-science/hunt-workbench/faq#terminal">workbench terminal</a> and run the following code to reset your <strong><code>r-base</code></strong> environment:</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mamba</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --yes</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-base&gt;=4.0,&lt;5.0&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;compilers&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;cmake&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;gmp&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-irkernel&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-devtools&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-remotes&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-dplyr&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-tidyverse&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-haven&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-languageserver&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-lintr&#39;</span></span></code></pre></div><p>If you have older setup without mamba, use Conda command instead:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> create</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --yes</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -n</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> r-base</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-base&gt;=4.0,&lt;5.0&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;compilers&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;cmake&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;gmp&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-irkernel&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-devtools&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-remotes&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-dplyr&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-tidyverse&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-haven&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-languageserver&#39;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &#39;r-lintr&#39;</span></span></code></pre></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>This removes custom packages that you might have installed.</p></div><h3 id="can-others-use-my-r-packages" tabindex="-1">Can others use my R packages? <a class="header-anchor" href="#can-others-use-my-r-packages" aria-label="Permalink to &quot;Can others use my R packages?&quot;">​</a></h3><p>When you have perfected your workflows, you can share your setup with your colleagues, although not directly.</p><p>The R-packages in your HUNT Workbench environment are usually not shared. This separation prevent others from breaking your packages and it allows you to explore packages without breaking the workflow of others.</p><p>So, to share environments you will need to export your environments definitions so others can reuse them:</p><p>Learn how to manage environments on <a href="https://docs.conda.io/projects/conda/en/latest/user-guide/tasks/manage-environments.html" target="_blank" rel="noreferrer">docs.conda.io</a>.</p><h3 id="how-do-i-reset-my-user-state-in-rstudio" tabindex="-1">How do I reset my user state in RStudio? <a class="header-anchor" href="#how-do-i-reset-my-user-state-in-rstudio" aria-label="Permalink to &quot;How do I reset my user state in RStudio?&quot;">​</a></h3><p>When you <a href="/do-science/hunt-workbench/faq#how-can-i-restart-my-workbench-environment">restart your HUNT Workbench</a>, your <em>RStudio</em> user state might still be preserved. Read the <em>RStudio</em> support pages about how you can <a href="https://support.rstudio.com/hc/en-us/articles/218730228-Resetting-a-user-s-state-on-RStudio-Workbench-RStudio-Server" target="_blank" rel="noreferrer">manually remove your user state</a> to achieve a complete reset.</p><h2 id="visual-studio-code" tabindex="-1">Visual Studio Code <a class="header-anchor" href="#visual-studio-code" aria-label="Permalink to &quot;Visual Studio Code&quot;">​</a></h2><h3 id="pre-installed-extensions" tabindex="-1">Pre-installed extensions <a class="header-anchor" href="#pre-installed-extensions" aria-label="Permalink to &quot;Pre-installed extensions&quot;">​</a></h3><p>VSCode in Workbench comes with jupyter and python extension.</p><h3 id="can-i-install-my-own-extensions" tabindex="-1">Can I install my own extensions? <a class="header-anchor" href="#can-i-install-my-own-extensions" aria-label="Permalink to &quot;Can I install my own extensions?&quot;">​</a></h3><p>No. This option is not available to user. If you need additional extensions <a href="/contact">contact us</a>.</p><h2 id="terminal" tabindex="-1">Terminal <a class="header-anchor" href="#terminal" aria-label="Permalink to &quot;Terminal&quot;">​</a></h2><h3 id="can-i-use-a-terminal-from-my-workbench" tabindex="-1">Can I use a terminal from my Workbench? <a class="header-anchor" href="#can-i-use-a-terminal-from-my-workbench" aria-label="Permalink to &quot;Can I use a terminal from my Workbench?&quot;">​</a></h3><p>Yes. You can find your <strong><code>Workbench Terminal</code></strong> in the main view of applications under the section: <strong><code>Other</code></strong>.</p><p><img src="`+k+'" alt="other_apps_terminal.png"></p><h3 id="when-do-i-need-to-use-a-terminal" tabindex="-1">When do I need to use a terminal? <a class="header-anchor" href="#when-do-i-need-to-use-a-terminal" aria-label="Permalink to &quot;When do I need to use a terminal?&quot;">​</a></h3><p><strong><code>Workbench Terminal</code></strong> can be handy when it comes to managing <a href="/do-science/tools/analytical/conda">Conda packages</a>, monitoring resources (<strong><code>htop</code></strong>), or handling other tasks.</p>',127)])])}const B=n(g,[["render",u]]);export{C as __pageData,B as default};
