import{_ as s}from"./chunks/JupyterLab.CNkl13mH.js";import{_ as i,c as n,o as r,aM as a}from"./chunks/framework.DAyGILei.js";const c="/assets/notepad-administrator.H7h9FWzU.png",l="/assets/notepad-admin-confirm.Cp0qrX0w.png",p="/assets/notepad-open-file.XIg6Bxg2.png",d="/assets/notepad-open-hosts-steps.C4MjpKoq.png",h="/assets/1_cert_p12.D3gSfNY7.png",u="/assets/2_cert_p12.BvYf2i6y.png",g="/assets/3_cert_p12.WMT00Upu.png",m="/assets/4_cert_p12.BDJLKMTt.png",f="/assets/5_cert_p12.qMCZV5-6.png",b="/assets/6_confirmCAcertificate.4udm9waX.png",y="/assets/7_confirmLabCAcertificate.Cg0m8rPl.png",k="/assets/8_import_success.D5vVDZR5.png",_="/assets/macventura1.CoDCiKnc.png",w="/assets/macventura2.D0br0Ezi.png",v="/assets/macventura3.BWjsicyr.png",t="/assets/macos_chrome.DDCOxRB2.png",C="/assets/macos-profiles-continue.DgJ7v9gq.png",T="/assets/macos-profiles-credentials.C9UxL0oO.png",o="/assets/import-ca-trust-websites.BM9fYyq1.png",x="/assets/chrome_select_certificate_confirm.BuSQIPI7.png",q="/assets/mac-firefox-certificates.BtRFKFDN.png",N="/assets/mac-firefox-import-cert.CVGHwDt4.png",S="/assets/mac-firefox-trust-ca.N35dlOIr.png",P=JSON.parse('{"title":"Installation","description":"Installation guide for HUNT Workbench.","frontmatter":{"title":"Installation","category":"HUNT Workbench","permalink":"/do-science/hunt-workbench/installation","outline":1,"description":"Installation guide for HUNT Workbench."},"headers":[],"relativePath":"do-science/hunt-workbench/installation.md","filePath":"do-science/hunt-workbench/installation.md"}'),U={name:"do-science/hunt-workbench/installation.md"};function I(W,e,A,H,F,D){return r(),n("div",null,[...e[0]||(e[0]=[a('<h1 id="hunt-workbench-installation" tabindex="-1">HUNT Workbench installation <a class="header-anchor" href="#hunt-workbench-installation" aria-label="Permalink to &quot;HUNT Workbench installation&quot;">​</a></h1><p><strong>This guide will help you to connect to your HUNT Workbench over a web browser.</strong></p><nav class="table-of-contents"><ul><li><a href="#_1-before-you-start">1. Before you start</a></li><li><a href="#_2-edit-your-hosts-file">2. Edit your hosts file</a></li><li><a href="#_3-install-your-certificates">3. Install your certificates</a></li><li><a href="#_4-open-your-hunt-workbench">4. Open your HUNT Workbench</a></li><li><a href="#immediate-troubleshooting">Immediate troubleshooting</a></li></ul></nav><div class="tip custom-block"><p class="custom-block-title">Check your onboarding email for Lab access link</p><p>We recommend that you follow the tailored guide in <strong>Lab access link</strong> for setting up <strong>Workbench access</strong> instead of this generic guide.</p></div><h2 id="_1-before-you-start" tabindex="-1">1. Before you start <a class="header-anchor" href="#_1-before-you-start" aria-label="Permalink to &quot;1. Before you start&quot;">​</a></h2><p>You will need the following to complete this guide:</p><div class="warning custom-block"><p class="custom-block-title">Requirements</p><ul><li>A working VPN and SSH configuration as described in our <a href="/do-science/lab-access/">Getting started</a> guide.</li><li>A HUNT Workbench TLS certificate as described in the <a href="/do-science/guides/access-guides/workbench-request#_3-verify-your-certificate">HUNT Workbench certificate request</a>.</li></ul><p>Without these it&#39;s impossible to complete this guide.</p></div><div class="tip custom-block"><p class="custom-block-title">Time</p><p>Allow for <strong>30 minutes</strong> to complete this installation and some playtime in your new environment.</p></div><h2 id="_2-edit-your-hosts-file" tabindex="-1">2. Edit your hosts file <a class="header-anchor" href="#_2-edit-your-hosts-file" aria-label="Permalink to &quot;2. Edit your hosts file&quot;">​</a></h2><p>First, let&#39;s set up your hosts file on your local computer. This allows you to connect to HUNT Workbench in your lab using a URL name, for example <strong><code>demolab.lab.hdc.ntnu.no</code></strong>, in your web-browser.</p><p>Select steps for your operating system:</p><p>::: expander &quot;Windows&quot; id=&quot;2-windows&quot;</p><ol><li><p>Press the Windows key.</p></li><li><p>Type <strong><code>Notepad</code></strong> in the search field.</p></li><li><p>In the search results, <strong>right-click</strong> Notepad and select Run as administrator.</p><p><img src="'+c+'" alt="notepad-administrator.png"></p><p><img src="'+l+'" alt="notepad-admin-confirm.png"></p></li><li><p>From Notepad, open the following file: <strong><code>C:\\Windows\\System32\\Drivers\\etc\\hosts</code></strong>.</p><p>Start by selecting <strong><code>File</code></strong> &gt; <strong><code>Open</code></strong>.</p><p><img src="'+p+'" alt="notepad-open-file.png"></p><p>Then find the (1.) directory and change <strong><code>Text documents</code></strong> to view (2) <strong><code>All files</code></strong>. When a file named <strong><code>hosts</code></strong> appears in the list (3), select it and click <strong><code>Open</code></strong> (4):</p><p><img src="'+d+'" alt="notepad-open-hosts-steps.png"></p></li><li><p>Add your <strong><code>hosts record</code></strong> to the text file. This is the line that you received from us in your HUNT Workbench onboarding email (look for Lab access link, the line is under point 6.9).</p></li><li><p>Select <strong><code>File</code></strong> &gt; <strong><code>Save</code></strong> to save your changes and close the <strong><code>Notepad</code></strong> application.</p></li></ol><p>:::</p><p>::: expander &quot;OS X and macOS&quot; id=&quot;2-osx-and-macos&quot;</p><ol><li>On your local computer, open your <strong><code>/etc/hosts</code></strong> file in your preferred text editor.</li></ol><p>Example with text editor:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>EDITOR=&#39;open -Wne&#39; sudo -e /etc/hosts</span></span></code></pre></div><p>Example with vim:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo vim /etc/hosts</span></span></code></pre></div><ol start="2"><li><p>Add (append) the <strong><code>hosts record</code></strong> line that you received from us on email (look for Lab access link, the line is under point 6.2).</p></li><li><p>Close your text editor.</p></li></ol><p>:::</p><p>::: expander &quot;Ubuntu Linux&quot; id=&quot;2-ubuntu-linux&quot;</p><ol><li>On your local computer, open your <strong><code>/etc/hosts</code></strong> file in your preferred text editor.</li></ol><p>Example with gedit:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo gedit /etc/hosts</span></span></code></pre></div><p>Example with vim:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>sudo vim /etc/hosts</span></span></code></pre></div><ol start="2"><li><p>Add (append) the <strong><code>hosts record</code></strong> line that you received from us on email (look for Lab access link, the line is under point 6.2).</p></li><li><p>Close your text editor.</p></li></ol><p>:::</p><h2 id="_3-install-your-certificates" tabindex="-1">3. Install your certificates <a class="header-anchor" href="#_3-install-your-certificates" aria-label="Permalink to &quot;3. Install your certificates&quot;">​</a></h2><p>Let&#39;s install the certificates that is required to allow traffic with your HUNT Workbench that is located in your lab.</p><p>::: expander &quot;Windows&quot; id=&quot;3-windows&quot;</p><ol><li><p>Open your <strong><code>p12 certificate</code></strong> that you downloaded from FileSender (the file will be called <code>&lt;username-labname&gt;</code> for example <strong><code>user-demolab</code></strong>). Make sure the store location is set to <strong><code>Current User</code></strong> and click <strong><code>Next</code></strong>:</p><p><img src="'+h+'" alt="1_cert_p12.png"></p></li><li><p>Click <strong><code>Next</code></strong> one more time.</p><p><img src="'+u+'" alt="2_cert_p12.png"></p></li><li><p>Fill in the <strong><code>TLS passphrase</code></strong> that you received on signal, and make sure the options are checked exactly as in the image. Then click <strong><code>Next</code></strong>:</p><p><img src="'+g+'" alt="3_cert_p12.png"></p></li><li><p>Check the first option: <em>Automatically select the certificate store based on the type of certificate</em>. Click <strong><code>Next</code></strong>:</p><p><img src="'+m+'" alt="4_cert_p12.png"></p></li><li><p>You should now get a <em>Completing the Certificate Import Wizard</em> message. Click <strong><code>Finish</code></strong> to complete the import:</p><p><img src="'+f+'" alt="5_cert_p12.png"></p></li><li><p>The wizard will require a confirmation to install a certification from us: <strong><code>HCTS CA 1</code></strong> (<em>HUNT Cloud Trust Services</em>). Our certificate is required to safely access your workbench environment.</p><p>You will normally be asked to confirm our certificate only when you set up the HUNT Workbench for the first time. Thus, if you see <strong><code>LAB</code></strong> instead of <strong><code>HCTS CA 1</code></strong> skip to step 7.</p><p>Before clicking <strong><code>YES</code></strong>, confirm that you see our thumbprint (fingerprint):</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>ADD9DFEC C998BE44 AC2F254E 75E5EB98 D91879A6</span></span></code></pre></div><p><img src="'+b+'" alt="6_confirmCAcertificate.png"></p></li><li><p>Similar to Step 6, you will also need to install a certificate for your lab (<strong><code>Lab CA</code></strong>), where your Lab name should appear. Click <strong><code>YES</code></strong>.</p><p><img src="'+y+'" alt="7_confirmLabCAcertificate.png"></p></li><li><p>Now quit your browser and restart it for the certificate to get recognized.</p><p><img src="'+k+'" alt="8_import_success.png"></p></li></ol><p>:::</p><p>::: expander &quot;macOS Ventura 13+&quot; id=&quot;3-macos&quot;</p><ol><li><p>Open your system profile config file that you got from FileSender (<strong><code>&lt;your_user_name&gt;.mobileconfig</code></strong>).</p></li><li><p>In <strong><code>System settings</code></strong> open section <strong><code>Privacy &amp; Security</code></strong>, scroll to the bottom of the page and select <strong><code>Profiles</code></strong></p><p><img src="'+_+'" alt="macventura1"></p></li><li><p>Select certificate required for installation.</p><p><img src="'+w+'" alt="macventura2"></p></li><li><p>Click <strong><code>Install</code></strong> when prompted. Then enter the <strong><code>TLS passphrase</code></strong> that you received on Signal and confirm.</p><p><img src="'+v+'" alt="macventura3"></p></li><li><p>Quit your browser and restart it again for the certificate to get recognized.</p></li><li><p>When you open your HUNT Workbench for the first time you will be asked for your local macOS password. This allows the browser to access your client certificate stored in your local Keychain. After filling in the password, confirm by clicking <strong><code>Always allow</code></strong> / <strong><code>Tillat alltid</code></strong>.</p><p><img src="'+t+'" alt="macos_chrome.png"></p></li></ol><p>:::</p><p>::: expander &quot;macOS Monterey and older&quot; id=&quot;3-macos-monterey-and-older&quot;</p><ol><li><p>Open your system profile config file that you got from FileSender (<strong><code>&lt;your_user_name&gt;.mobileconfig</code></strong>).</p></li><li><p>In <strong><code>System Preferences</code></strong> open section <strong><code>Profiles</code></strong>.</p><div class="warning custom-block"><p class="custom-block-title">NTNU managed MacBook</p><p>If your MacBook is managed by NTNU and you do not see section Profiles in your System Preferences you will need to request access to Profile through NTNU Hjelp. If you are not NTNU affiliated, can contact your IT department for assistance.</p></div></li><li><p>Click <strong><code>Continue</code></strong> to confirm your profile installation.</p><p><img src="'+C+'" alt="macos-profiles-continue.png"></p></li><li><p>Enter the <strong><code>TLS passphrase</code></strong> that you received on Signal and the click <strong><code>Install</code></strong> to confirm.</p><p><img src="'+T+'" alt="macos-profiles-credentials.png"></p></li><li><p>Quit your browser and restart it again for the certificate to get recognized.</p></li><li><p>When you open your HUNT Workbench for the first time you will be asked for your local macOS password. This allows the browser to access your client certificate stored in your local Keychain. After filling in the password, confirm by clicking <strong><code>Always allow</code></strong> / <strong><code>Tillat alltid</code></strong>.</p><p><img src="'+t+'" alt="macos_chrome.png"></p></li></ol><p>:::</p><p>::: expander &quot;Ubuntu Linux&quot; id=&quot;3-ubuntu-linux&quot;</p><p>We recommend that you use the <a href="https://www.google.com/chrome/" target="_blank" rel="noreferrer">Google Chrome browser</a> for predictable access to your HUNT Workbench.</p><ol><li><p>Open <strong><code>Google Chrome</code></strong> on your local computer.</p></li><li><p>Download our public CA certificate from <a href="https://pki.hdc.ntnu.no/hctsca1.crt" target="_blank" rel="noreferrer">https://pki.hdc.ntnu.no/hctsca1.crt</a></p></li><li><p>In Google Chrome, open the URL <strong><code>chrome://settings/certificates</code></strong> and navigate to section <strong><code>Authorities</code></strong> then click the <strong><code>Import</code></strong> button on the right side of the screen to import <code>hctsca1.crt</code> certificate file. Select first option <code>Trust this certificate for identifying websites</code>.</p></li></ol><p><img src="'+o+`" alt="import-ca-trust-websites.png"></p><ol start="4"><li><p>Within <strong><code>chrome://settings/certificates</code></strong> navigate to section <strong><code>Your certificates</code></strong> and click the <strong><code>Import</code></strong> button on the right side of the screen.</p></li><li><p>Browse and select your <strong><code>.p12</code></strong> file that you downloaded to your local computer (the file will be called <code>&lt;username-labname&gt;</code> for example <strong><code>user-demolab</code></strong>), and enter the <strong><code>TLS passphrase</code></strong> that we sent you on Signal.</p></li><li><p>Restart Chrome.</p></li></ol><p>:::</p><h2 id="_4-open-your-hunt-workbench" tabindex="-1">4. Open your HUNT Workbench <a class="header-anchor" href="#_4-open-your-hunt-workbench" aria-label="Permalink to &quot;4. Open your HUNT Workbench&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Make sure you are connected to the VPN before you access your HUNT Workbench.</p></div><ol><li><p>Open your web browser (we recommend <a href="https://www.google.com/chrome/" target="_blank" rel="noreferrer">Google Chrome browser</a>).</p></li><li><p>Type in the URL address to your lab in your browser.</p></li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Principal example</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">https://</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">&lt;labname&gt;.lab.hdc.ntnu.no</span></span>
<span class="line"></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># -- Demo example</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">https://demolab.lab.hdc.ntnu.no</span></span></code></pre></div><p>You may get a <strong><code>User Identification Request</code></strong> for your new certificate. Verify that the certificates are issued by: (Click Certificate information &gt; Details and look for names given under)</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">Organization:</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;HUNT Cloud&quot;</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">Issued</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> Under:</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;HUNT Cloud Trust Services&quot;</span></span></code></pre></div><p>If given the option, ensure that the <strong><code>Remember this decision</code></strong> box is checked, and click <strong><code>OK</code></strong>.</p><p><img src="`+x+'" alt="chrome_select_certificate_confirm.png"></p><ol start="3"><li><p>Sign in with your HUNT Cloud lab username and lab passphrase. This is the passphrase that you made on your first login.</p></li><li><p>With a little bit of luck you should now see your new HUNT Workbench. <strong>Click around and explore your new world!</strong></p></li></ol><p><img src="'+s+`" alt="hunt-lab-workbench.png"></p><div class="tip custom-block"><p class="custom-block-title">Further reading</p><p>See our <a href="/do-science/hunt-workbench/principles">HUNT Workbench principles</a> section to learn more about your new environment. For more practical advises on how to install your Python/R packages head over to <a href="/do-science/hunt-workbench/faq">Workbench FAQ</a>.</p></div><h2 id="immediate-troubleshooting" tabindex="-1">Immediate troubleshooting <a class="header-anchor" href="#immediate-troubleshooting" aria-label="Permalink to &quot;Immediate troubleshooting&quot;">​</a></h2><p>This section includes issues that you might encounter during your first setup. See our HUNT Workbench <a href="/do-science/hunt-workbench/faq">FAQ</a> and HUNT Workbench <a href="/do-science/hunt-workbench/troubleshooting">Troubleshooting</a> if you do not find your answers below.</p><h4 id="this-site-can-t-be-reached" tabindex="-1">This site can&#39;t be reached <a class="header-anchor" href="#this-site-can-t-be-reached" aria-label="Permalink to &quot;This site can&#39;t be reached&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>If you are getting <code>DNS_PROBE_FINISHED_NXDOMAIN</code> error you need to repeat the <a href="#_2-edit-your-hosts-file">Step 2</a> of this guide.</p><p>On MacOS or Linux you can also assure hosts return IP address using the command below:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># -- Principal example</span></span>
<span class="line"><span>getent hosts &lt;labname&gt;.lab.hdc.ntnu.no</span></span>
<span class="line"><span></span></span>
<span class="line"><span># -- Demo example</span></span>
<span class="line"><span>getent hosts demolab.lab.hdc.ntnu.no</span></span></code></pre></div></details><h4 id="i-don-t-remember-my-passphrase" tabindex="-1">I don&#39;t remember my passphrase <a class="header-anchor" href="#i-don-t-remember-my-passphrase" aria-label="Permalink to &quot;I don&#39;t remember my passphrase&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>Don&#39;t worry. Request a <a href="/do-science/service-desk/#ssh-passphrase-reset">SSH passphrase rest</a> in our do-science service desk.</p></details><h4 id="firefox-did-not-connect" tabindex="-1">Firefox - Did Not Connect <a class="header-anchor" href="#firefox-did-not-connect" aria-label="Permalink to &quot;Firefox - Did Not Connect&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p><em>(We recommend to use Google Chrome as your HUNT Workbench browser.)</em></p><p>Firefox may require that you manually import the <strong><code>HUNT Cloud Certificate Authority</code></strong> to consider it trusted.</p><p>If you see Error code: <strong><code>SEC_ERROR_UNKNOWN_ISSUER</code></strong> when accessing Workbench follow these steps:</p><ol><li><p>Download our public CA certificate from <a href="https://pki.hdc.ntnu.no/hctsca1.crt" target="_blank" rel="noreferrer">https://pki.hdc.ntnu.no/hctsca1.crt</a></p></li><li><p>Open the following Firefox URL: <strong><code>about:preferences#privacy</code></strong></p></li><li><p>Scroll down to section <strong><code>Certificates</code></strong> and click on <strong><code>View Certificates</code></strong>.</p><p><img src="`+q+'" alt="mac-firefox-certificates.png"></p></li><li><p>Switch to tab <strong><code>Authorities</code></strong> and click on <strong><code>Import</code></strong>.</p><p><img src="'+N+'" alt="mac-firefox-import-cert.png"></p></li><li><p>Select <strong><code>hctsca1.crt</code></strong> and check option <strong><code>Trust this CA to identify websites</code></strong>.</p><p><img src="'+S+'" alt="mac-firefox-trust-ca.png"></p></li></ol></details><h4 id="chrome-on-ubuntu" tabindex="-1">Chrome on Ubuntu <a class="header-anchor" href="#chrome-on-ubuntu" aria-label="Permalink to &quot;Chrome on Ubuntu&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>If you are getting <code>ERR_CERT_AUTHORITY_INVALID</code> error:</p><ol><li><p>In Google Chrome, open the URL <strong><code>chrome://settings/certificates</code></strong> and navigate to section <strong><code>Authorities</code></strong></p></li><li><p>Search for HUNT Cloud certificates (<code>org-HUNT Cloud Trust Services</code>)</p></li><li><p>Edit the HCTS CA 1 certificate and select first option <code>Trust this certificate for identifying websites</code>.</p></li></ol><p><img src="'+o+'" alt="import-ca-trust-websites.png"></p></details><h4 id="_502-bad-gateway" tabindex="-1">502 Bad gateway <a class="header-anchor" href="#_502-bad-gateway" aria-label="Permalink to &quot;502 Bad gateway&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>A <strong><code>502 Bad gateway</code></strong> error is an indication that something is wrong with the configuration on the server side. <a href="/contact">Contact us</a> in your lab channel on Slack or email for further investigations.</p></details>',70)])])}const B=i(U,[["render",I]]);export{P as __pageData,B as default};
