import{_ as e,c as s,o as t,aM as i}from"./chunks/framework.DAyGILei.js";const n="/assets/Preferences_and_MATLAB_R2022b_-_academic_use-2.DQTE3coL.png",k=JSON.parse('{"title":"MATLAB","description":"Installation guide for MATLAB.","frontmatter":{"title":"MATLAB","category":"Analytical tools","permalink":"/do-science/tools/analytical/MATLAB","outline":1,"description":"Installation guide for MATLAB."},"headers":[],"relativePath":"do-science/tools/analytical/matlab.md","filePath":"do-science/tools/analytical/matlab.md"}'),o={name:"do-science/tools/analytical/matlab.md"};function l(r,a,h,c,d,p){return t(),s("div",null,[...a[0]||(a[0]=[i(`<h1 id="matlab" tabindex="-1">MATLAB <a class="header-anchor" href="#matlab" aria-label="Permalink to &quot;MATLAB&quot;">​</a></h1><p><strong><a href="https://www.mathworks.com/" target="_blank" rel="noreferrer">MATLAB</a> is a computing environment developed by MatWorks for efficient matrix manipulations and other handy computing tools.</strong></p><p>This guide covers the installation of MATLAB on unmanaged On-demand and BLUE machines. <a href="/do-science/contact">Contact us</a> for installations on your <a href="/do-science/faq/compute#machine-types">home machine</a>.</p><div class="tip custom-block"><p class="custom-block-title">MATLAB in HUNT Workbench</p><p>Head over to the <a href="/do-science/hunt-workbench/faq#matlab">MATLAB FAQ</a> in our HUNT Workbench section to learn more about the setup in your workbench environment.</p></div><nav class="table-of-contents"><ul><li><a href="#_1-download">1. Download</a></li><li><a href="#_2-activate-the-license">2. Activate the license</a></li><li><a href="#_3-install">3. Install</a></li><li><a href="#_4-run">4. Run</a></li><li><a href="#troubleshooting">Troubleshooting</a></li></ul></nav><div class="warning custom-block"><p class="custom-block-title">Requirements</p><ul><li>An unmanaged On-demand or BLUE machine where you have root access.</li><li>A working <a href="/do-science/tools/technical/x2go">X2Go server</a> installation on this on this machine.</li><li>Access to the MATLAB software binaries for Linux from your organizaton.</li><li>Access to a MATLAB license for the users in your lab.</li></ul></div><h2 id="_1-download" tabindex="-1">1. Download <a class="header-anchor" href="#_1-download" aria-label="Permalink to &quot;1. Download&quot;">​</a></h2><p>1.1 Download the MATLAB software for Linux on your local computer (client). We recommend that you choose a stable <code>beta</code> version (for example, 2022b) to ensure a reliable installation.</p><div class="tip custom-block"><p class="custom-block-title">For NTNU users</p><p>Log into <a href="https://software.ntnu.no/ntnu/MATLAB" target="_blank" rel="noreferrer">NTNU&#39;s software repository for MATLAB</a>, choose your desired version in the top right corner and download on your local computer.</p></div><p>1.2. Transfer MATLAB to your preferred folder in your lab machine (for example <code>/mnt/scratch/MATLAB/</code>)</p><p>1.3. Log into your lab machine and unpack the zip file in your folder.</p><p>Install dependencies:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> unzip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> libnss3</span></span></code></pre></div><p>Unzip MATLAB installer:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">unzip</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> &lt;</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">MATLABfile.zi</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">p</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">&gt;</span></span></code></pre></div><ol start="4"><li>Make all files in your MATLAB-folder executables: <code>chmod -R 777 &lt;MATLAB-folder&gt;</code> (e.g. /mnt/scratch/MATLAB/). This is a simple fix so you can run the files within subfolders as required by MATLAB.</li></ol><h2 id="_2-activate-the-license" tabindex="-1">2. Activate the license <a class="header-anchor" href="#_2-activate-the-license" aria-label="Permalink to &quot;2. Activate the license&quot;">​</a></h2><p>2.1 Identify the required license information from your host organization. The content is dependent on your organizations license.</p><div class="tip custom-block"><p class="custom-block-title">For NTNU users</p><p>The exact content of this files is described in <a href="https://software.ntnu.no/ntnu/MATLAB" target="_blank" rel="noreferrer">NTNU&#39;s repository for MATLAB</a>.</p></div><p>2.2 Create a file named <code>license.dat</code> with your license information in your <code>&lt;MATLAB-folder&gt;</code>.</p><p>In the principal example below, you will need you to replace <code>&lt;license-server-address&gt;</code>, <code>&lt;key1&gt;</code> and <code>&lt;key2&gt;</code> with an URL-address and strings provided by your host institutions license:</p><div class="language-ini vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">ini</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">SERVER &lt;license-server-address&gt; &lt;key1&gt; &lt;key2&gt;</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">USE_SERVER</span></span></code></pre></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Make sure you add a new/empty line after &quot;USE_SERVER&quot;.</p></div><h2 id="_3-install" tabindex="-1">3. Install <a class="header-anchor" href="#_3-install" aria-label="Permalink to &quot;3. Install&quot;">​</a></h2><p>The below steps require the code to be run in <code>xterm</code> started from X2GO on the machine holding the MATLAB files downloaded in the above steps.</p><p>3.1. Prepare target directory:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/work/software/matlab_r2022b</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/tmp</span></span></code></pre></div><p>3.2 Go to your MATLAB installation folder (e.g. /mnt/scratch/software/matlab_r2022b) and run the installer:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">bash</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -downloadFolder</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/software/matlab_r2022b</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -tmpdir</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/tmp</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -destinationFolder</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/work/software/matlab_r2022b</span></span></code></pre></div><p>Note that you will need to update the MATLAB version in the above code to the version your are installing.</p><p>3.3 During the installation choose: <code>Use a File Installation Key</code></p><p>3.4. Choose <code>I have the File Installation Key for my license</code> and enter the key (these are found on you software repository)</p><p>3.5. Follow the installer and choose the packages you want.</p><p>3.6. When the installer asks for your product activation, choose the <code>non internet</code> option and provide the location of the <code>license.dat</code> file previously generated.</p><p>3.7. If successful, your should see <code>product activation message</code>.</p><h2 id="_4-run" tabindex="-1">4. Run <a class="header-anchor" href="#_4-run" aria-label="Permalink to &quot;4. Run&quot;">​</a></h2><p><strong>Run in command line</strong></p><p>If you&#39;re fine with command lines, run <code>MATLAB</code> directly in your shell. Read more in <a href="https://se.mathworks.com/help/matlab/ref/matlablinux.html" target="_blank" rel="noreferrer">MathWorks documentation</a>.</p><p><strong>Run in the graphical interface</strong></p><p>If you prefer MATLAB&#39;s graphical interface, run <code>MATLAB</code> in your terminal (<code>xterm</code>) started in <a href="/do-science/tools/technical/x2go">X2GO</a>.</p><p>Open X2Go, connect to your lab machine where you installed MATLAB and run the following command in <code>xterm</code>:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/usr/bin/xterm</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -e</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> matlab</span></span></code></pre></div><p>or with full path e.g.:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/usr/bin/xterm</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -e</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/work/software/matlab_r2022b/bin/matlab</span></span></code></pre></div><p>For the example above, you will need to update the MATLAB version to the one you just installed.</p><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p>Here are are collection of known issues.</p><h4 id="why-can-t-matlab-find-the-packages-i-choose-to-install" tabindex="-1">Why can&#39;t MATLAB find the packages I choose to install? <a class="header-anchor" href="#why-can-t-matlab-find-the-packages-i-choose-to-install" aria-label="Permalink to &quot;Why can&#39;t MATLAB find the packages I choose to install?&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>One known issue is that sometimes the latest MATLAB version compiled by some universities do miss some packages. Try to install a slightly older version. <a href="/do-science/contact">Contact us</a> for assistance if this do not work.</p></details><h4 id="can-i-install-the-code-without-sudo" tabindex="-1">Can I install the code without sudo? <a class="header-anchor" href="#can-i-install-the-code-without-sudo" aria-label="Permalink to &quot;Can I install the code without sudo?&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>No, not in an easy way. Thus, either we have to install on <code>home</code> machines, or your can install these yourself on unmanaged iaas and blue machines where you get root. <a href="/do-science/contact">Contact us</a> if you need MATLAB on your home machine.</p></details><h4 id="my-product-activation-do-not-seem-to-work" tabindex="-1">My product activation do not seem to work? <a class="header-anchor" href="#my-product-activation-do-not-seem-to-work" aria-label="Permalink to &quot;My product activation do not seem to work?&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>We need to open for traffic from your lab to your organization&#39;s license server. <a href="/do-science/contact">Contact us</a> for network openings.</p></details><h4 id="why-is-the-live-editor-not-working" tabindex="-1">Why is the live editor not working? <a class="header-anchor" href="#why-is-the-live-editor-not-working" aria-label="Permalink to &quot;Why is the live editor not working?&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>If MATLAB complains that Live editor is unable to run in the current system configuration it is important to check if libnss3 is installed.</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">dpkg</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -s</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> libnss3</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"># If not installed run</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> libnss3</span></span></code></pre></div></details><h4 id="why-is-my-matlab-installation-stuck" tabindex="-1">Why is my MATLAB installation stuck? <a class="header-anchor" href="#why-is-my-matlab-installation-stuck" aria-label="Permalink to &quot;Why is my MATLAB installation stuck?&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>If the installation was progressing and then stuck at some point it is important to check disk space <code>df -h</code>. In some cases <code>/tmp</code> directory may get filled up. We usually recommend setting alternative tmp before running install:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/scratch/tmp</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">export</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> TMP</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">/mnt/scratch/tmp</span></span>
<span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">export</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> TEMP_DIR</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">/mnt/scratch/tmp</span></span></code></pre></div></details><h4 id="macos-cannot-copy-paste-in-matlab" tabindex="-1">MacOS - cannot copy-paste in MATLAB <a class="header-anchor" href="#macos-cannot-copy-paste-in-matlab" aria-label="Permalink to &quot;MacOS - cannot copy-paste in MATLAB&quot;">​</a></h4><details class="details custom-block"><summary>Read more</summary><p>If standard copy-pasting is not working for you, you might want to change your keyboard shortcuts settings in MATLAB</p><p><code>Matlab --&gt; Preferences --&gt; Keyboard --&gt; Shortcuts --&gt; select preffered keyboard</code></p><p><img src="`+n+'" alt="image_1"></p></details>',59)])])}const m=e(o,[["render",l]]);export{k as __pageData,m as default};
