import{_ as s,c as i,o as e,aM as t}from"./chunks/framework.DAyGILei.js";const c=JSON.parse('{"title":"PLINK","description":"Installation guide for Plink.","frontmatter":{"title":"PLINK","category":"Analytical tools","permalink":"/do-science/tools/analytical/plink","outline":1,"description":"Installation guide for Plink."},"headers":[],"relativePath":"do-science/tools/analytical/plink.md","filePath":"do-science/tools/analytical/plink.md"}'),n={name:"do-science/tools/analytical/plink.md"};function l(p,a,h,o,r,d){return e(),i("div",null,[...a[0]||(a[0]=[t('<h1 id="plink" tabindex="-1">PLINK <a class="header-anchor" href="#plink" aria-label="Permalink to &quot;PLINK&quot;">​</a></h1><p><a href="http://zzz.bwh.harvard.edu/plink/" target="_blank" rel="noreferrer">PLINK</a> is a free, open-source whole genome association analysis toolset, designed to perform a range of basic, large-scale analyses in a computationally efficient manner.</p><nav class="table-of-contents"><ul><li><a href="#how-to-install-plink-1">How to install PLINK 1</a></li><li><a href="#how-to-install-plink-1-9">How to install PLINK 1.9</a></li><li><a href="#how-to-install-plink-2">How to install PLINK 2</a><ul><li><a href="#using-conda">Using conda</a></li><li><a href="#using-binary-release">Using binary release</a></li></ul></li><li><a href="#troubleshooting">Troubleshooting</a></li></ul></nav><h2 id="how-to-install-plink-1" tabindex="-1">How to install PLINK 1 <a class="header-anchor" href="#how-to-install-plink-1" aria-label="Permalink to &quot;How to install PLINK 1&quot;">​</a></h2><p>Install <a href="http://zzz.bwh.harvard.edu/plink/" target="_blank" rel="noreferrer">PLINK 1</a> with the following command:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> plink</span></span></code></pre></div><p>And use the following executable:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">plink1</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --noweb</span></span></code></pre></div><h2 id="how-to-install-plink-1-9" tabindex="-1">How to install PLINK 1.9 <a class="header-anchor" href="#how-to-install-plink-1-9" aria-label="Permalink to &quot;How to install PLINK 1.9&quot;">​</a></h2><p>Install <a href="https://www.cog-genomics.org/plink/1.9/" target="_blank" rel="noreferrer">PLINK 1.9</a> with the following commands:</p><p>Make sure you have your personal bin directory in place:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> &amp;&amp; </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">source</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/.profile</span></span></code></pre></div><p>Download the latest binary release of PLINK 1.9:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wget</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -P</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> https://s3.amazonaws.com/plink1-assets/plink_linux_x86_64_20231211.zip</span></span></code></pre></div><p>Unpack the archive:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">unzip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  ~/bin/plink_linux_x86_64_20231211.zip</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -d</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/</span></span></code></pre></div><p>Remove archive:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">rm</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -v</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/plink_linux_x86_64_20231211.zip</span></span></code></pre></div><p>Test by printing the version:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">plink</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --version</span></span></code></pre></div><p>You can also install PLINK 1.9 with Bioconda. See our <a href="./conda">Anaconda and Bioconda</a> tutorial for more details.</p><h2 id="how-to-install-plink-2" tabindex="-1">How to install PLINK 2 <a class="header-anchor" href="#how-to-install-plink-2" aria-label="Permalink to &quot;How to install PLINK 2&quot;">​</a></h2><h3 id="using-conda" tabindex="-1">Using conda <a class="header-anchor" href="#using-conda" aria-label="Permalink to &quot;Using conda&quot;">​</a></h3><p>Installing <a href="https://www.cog-genomics.org/plink/2.0/" target="_blank" rel="noreferrer">PLINK 2</a> is a little more cumbersome as it is not available in Ubuntu&#39;s package repositories.</p><p>You can either use <a href="/do-science/tools/analytical/conda">Conda</a> to install <a href="https://anaconda.org/bioconda/plink2" target="_blank" rel="noreferrer">Plink2 package</a> or follow with binary release below.</p><p>To install package into your <a href="/do-science/tools/analytical/conda">Conda</a> environment use this command:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">conda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -c</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bioconda</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> plink2</span></span></code></pre></div><h3 id="using-binary-release" tabindex="-1">Using binary release <a class="header-anchor" href="#using-binary-release" aria-label="Permalink to &quot;Using binary release&quot;">​</a></h3><p>To download and unpack the binary for PLINK 2 follow these steps.</p><p>Make sure you have your personal bin directory in place:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">mkdir</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -p</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> &amp;&amp; </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">source</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/.profile</span></span></code></pre></div><p>Download the latest binary release of PLINK 2:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">wget</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -P</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> http://s3.amazonaws.com/plink2-assets/plink2_linux_x86_64_latest.zip</span></span></code></pre></div><p>Unzip the archive:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">unzip</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/plink2_linux_x86_64_latest.zip</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -d</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/</span></span></code></pre></div><p>Remove archive:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">rm</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -v</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ~/bin/plink2_linux_x86_64_latest.zip</span></span></code></pre></div><p>You can now use the following executable:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">plink2</span></span></code></pre></div><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p>Install unzip if missing:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -y</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> unzip</span></span></code></pre></div>',42)])])}const g=s(n,[["render",l]]);export{c as __pageData,g as default};
