import{_ as a,c as i,o as n,aM as t}from"./chunks/framework.DAyGILei.js";const g=JSON.parse('{"title":"Regenie","description":"Installation guide for Regenie","frontmatter":{"title":"Regenie","category":"Analytical tools","permalink":"/do-science/tools/analytical/regenie","outline":1,"description":"Installation guide for Regenie"},"headers":[],"relativePath":"do-science/tools/analytical/regenie.md","filePath":"do-science/tools/analytical/regenie.md"}'),o={name:"do-science/tools/analytical/regenie.md"};function s(r,e,l,c,d,p){return n(),i("div",null,[...e[0]||(e[0]=[t(`<h1 id="regenie" tabindex="-1">Regenie <a class="header-anchor" href="#regenie" aria-label="Permalink to &quot;Regenie&quot;">​</a></h1><p><strong><a href="https://github.com/rgcgithub/regenie" target="_blank" rel="noreferrer">Regenie</a> is a C++ program for whole genome regression modelling of large genome-wide association studies. Regenie is developed and supported by a team of scientists at the Regeneron Genetics Center.</strong></p><nav class="table-of-contents"><ul><li><a href="#properties">Properties</a></li><li><a href="#requirements">Requirements</a></li><li><a href="#installation">Installation</a></li><li><a href="#genome-wide-association-analysis">Genome-wide association analysis</a></li></ul></nav><h2 id="properties" tabindex="-1">Properties <a class="header-anchor" href="#properties" aria-label="Permalink to &quot;Properties&quot;">​</a></h2><p>The method has the following properties:</p><ul><li>It works on quantitative and binary traits, including binary traits with unbalanced case-control ratios</li><li>It can process multiple phenotypes at once</li><li>It is fast and memory efficient</li><li>For binary traits it supports Firth logistic regression and an SPA test</li><li>It supports the BGEN, PLINK bed/bim/fam and PLINK2 pgen/pvar/psam genetic data formats</li></ul><h2 id="requirements" tabindex="-1">Requirements <a class="header-anchor" href="#requirements" aria-label="Permalink to &quot;Requirements&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">Requirements</p><ul><li>Conda (miniconda), see our <a href="/do-science/tools/analytical/conda">Conda tutorial</a> for more details.</li><li>Conda channels: conda-forge, bioconda</li></ul></div><p>You will need bioconda and conda-forge channels. If you have not set channels yet make sure to add them in this order:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>conda config --add channels bioconda</span></span>
<span class="line"><span>conda config --add channels conda-forge</span></span></code></pre></div><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>Install regenie into your environment:</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>conda install -c bioconda regenie</span></span></code></pre></div><h2 id="genome-wide-association-analysis" tabindex="-1">Genome-wide association analysis <a class="header-anchor" href="#genome-wide-association-analysis" aria-label="Permalink to &quot;Genome-wide association analysis&quot;">​</a></h2><p>We recommend <a href="https://rgcgithub.github.io/regenie/options/" target="_blank" rel="noreferrer">Regenie documentation</a> to read up on gwas analysis using Regenie. For more details we recommend having a look on the official paper <a href="https://www.biorxiv.org/content/10.1101/2020.06.19.162354v2.abstract" target="_blank" rel="noreferrer">Computationally efficient whole genome regression for quantitative and binary traits</a>, where Regenie was first introduced.</p>`,15)])])}const u=a(o,[["render",s]]);export{g as __pageData,u as default};
