import{_ as e,c as s,o as i,aM as n}from"./chunks/framework.DAyGILei.js";const t="/assets/ncdu-example.BTmLauQD.png",g=JSON.parse('{"title":"NCDU","description":"Usage guide for NCDU.","frontmatter":{"title":"NCDU","category":"Technical tools","permalink":"/do-science/tools/technical/ncdu","outline":1,"description":"Usage guide for NCDU."},"headers":[],"relativePath":"do-science/tools/technical/ncdu.md","filePath":"do-science/tools/technical/ncdu.md"}'),l={name:"do-science/tools/technical/ncdu.md"};function o(r,a,c,h,u,d){return i(),s("div",null,[...a[0]||(a[0]=[n('<h1 id="ncdu" tabindex="-1">NCDU <a class="header-anchor" href="#ncdu" aria-label="Permalink to &quot;NCDU&quot;">​</a></h1><p><strong><a href="https://dev.yorhel.nl/ncdu/man" target="_blank" rel="noreferrer">Ncdu</a> is a disk usage analyzer that you can use to find space hogs in your lab.</strong></p><p>Ncdu aims to be fast, simple and easy to use. It uses a ncurses interface, and should be able to run in any minimal POSIX-like environment with ncurses installed.</p><p>Although this guide for your lab, Ncdu is a useful tool even on regular desktop systems such as OS X and MacOS and Linux.</p><nav class="table-of-contents"><ul><li><a href="#installation">Installation</a></li><li><a href="#usage">Usage</a><ul><li><a href="#general-usage">General usage</a></li><li><a href="#analyse-your-root-volume">Analyse your root volume</a></li><li><a href="#analyze-specific-volumes">Analyze specific volumes</a></li></ul></li></ul></nav><h2 id="installation" tabindex="-1">Installation <a class="header-anchor" href="#installation" aria-label="Permalink to &quot;Installation&quot;">​</a></h2><p>Log into your lab and install <code>ncdu</code> using the following command:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ncdu</span></span></code></pre></div><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h2><h3 id="general-usage" tabindex="-1">General usage <a class="header-anchor" href="#general-usage" aria-label="Permalink to &quot;General usage&quot;">​</a></h3><p>Start <code>ncdu</code> in the directory that you want to analyze:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">ncdu</span></span></code></pre></div><p>Depending on your volume sizes and number of files, the initial scanning may last from seconds to several minutes.</p><p><img src="'+t+'" alt="Ncdu example"></p><p>This is an example of a scan of a <strong><code>/mnt/work</code></strong> volume where the software (46GB) and workbench (23GB) folders take up most of your space.</p><p>If you see that your folders are nearing full, we would recommend either a clean up of files in the lab or an expansion request</p><h3 id="analyse-your-root-volume" tabindex="-1">Analyse your root volume <a class="header-anchor" href="#analyse-your-root-volume" aria-label="Permalink to &quot;Analyse your root volume&quot;">​</a></h3><p>To find what&#39;s taking space on your root volume (/), use the <strong><code>-x</code></strong> paramter. This excludes other volumes mounted on your machine (such as /mnt/work):</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">ncdu</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -x</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /</span></span></code></pre></div><h3 id="analyze-specific-volumes" tabindex="-1">Analyze specific volumes <a class="header-anchor" href="#analyze-specific-volumes" aria-label="Permalink to &quot;Analyze specific volumes&quot;">​</a></h3><p>You may use this example to analyze disk usage on specific volumes:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">ncdu</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -x</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> /mnt/archive</span></span></code></pre></div>',22)])])}const m=e(l,[["render",o]]);export{g as __pageData,m as default};
