import{_ as a,c as i,o as e,aM as t}from"./chunks/framework.DAyGILei.js";const u=JSON.parse('{"title":"VNC","description":"Usage guide for VNC.","frontmatter":{"title":"VNC","category":"Technical tools","permalink":"/do-science/tools/technical/vnc","outline":1,"description":"Usage guide for VNC."},"headers":[],"relativePath":"do-science/tools/technical/vnc.md","filePath":"do-science/tools/technical/vnc.md"}'),n={name:"do-science/tools/technical/vnc.md"};function l(o,s,r,h,p,c){return e(),i("div",null,[...s[0]||(s[0]=[t(`<h1 id="vnc" tabindex="-1">VNC <a class="header-anchor" href="#vnc" aria-label="Permalink to &quot;VNC&quot;">​</a></h1><p><strong><a href="https://en.wikipedia.org/wiki/Virtual_Network_Computing" target="_blank" rel="noreferrer">VNC</a> is a graphical desktop-sharing system that allow you to connect to graphical interfaces in your lab machines.</strong></p><nav class="table-of-contents"><ul><li><a href="#_1-install-turbovnc">1. Install TurboVNC</a></li><li><a href="#_2-install-a-desktop-environment">2. Install a desktop environment</a></li><li><a href="#_3-start-turbovnc">3. Start TurboVNC</a></li><li><a href="#_4-configure-your-local-computer">4. Configure your local computer</a></li><li><a href="#useful-commands-for-turbo-vnc">Useful commands for Turbo VNC</a><ul><li><a href="#list-running-sessions">List running sessions</a></li><li><a href="#stop-session">Stop session</a></li></ul></li><li><a href="#troubleshooting">Troubleshooting</a></li></ul></nav><h2 id="_1-install-turbovnc" tabindex="-1">1. Install TurboVNC <a class="header-anchor" href="#_1-install-turbovnc" aria-label="Permalink to &quot;1. Install TurboVNC&quot;">​</a></h2><p>1.1. Log into your lab machine.</p><p>1.2. Download the latest TurboVNC</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># -- Example for version 3.0.2 </span></span>
<span class="line"><span>wget https://netix.dl.sourceforge.net/project/turbovnc/3.0.2/turbovnc_3.0.2_amd64.deb</span></span></code></pre></div><p><a href="https://sourceforge.net/projects/turbovnc/files/" target="_blank" rel="noreferrer">Click here</a> to see the latest version and update the text above with a new version number if needed.</p><p>1.3. Install</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> ./turbovnc_3.0.2_amd64.deb</span></span></code></pre></div><h2 id="_2-install-a-desktop-environment" tabindex="-1">2. Install a desktop environment <a class="header-anchor" href="#_2-install-a-desktop-environment" aria-label="Permalink to &quot;2. Install a desktop environment&quot;">​</a></h2><p>Make sure you have a desktop environment setup that TigerVNC can connect towards:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> update</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -y</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> &amp;&amp; </span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> autoremove</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -y</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt-get</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -y</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --no-install-recommends</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> xubuntu-desktop</span></span></code></pre></div><h2 id="_3-start-turbovnc" tabindex="-1">3. Start TurboVNC <a class="header-anchor" href="#_3-start-turbovnc" aria-label="Permalink to &quot;3. Start TurboVNC&quot;">​</a></h2><p>Start the session on your server with the command below and note the number of session N (e.g. <code>:1</code>):</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/opt/TurboVNC/bin/vncserver</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -geometry</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> 1280x900</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -SecurityTypes</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> None</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -localhost</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -disconnect</span></span></code></pre></div><h2 id="_4-configure-your-local-computer" tabindex="-1">4. Configure your local computer <a class="header-anchor" href="#_4-configure-your-local-computer" aria-label="Permalink to &quot;4. Configure your local computer&quot;">​</a></h2><p>You will need a SSH tunnel between your local computer and your lab to connect to your VNC session.</p><div class="tip custom-block"><p class="custom-block-title">Calculate your port</p><p>In this example we will use port 5901. In case of multiple users you will need to calculate your port based your session number identified above (N): 5900 + n(:1) =&gt; 5901.</p></div><p>4.1 In your local computer, establish a SSH tunnel to your lab:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">ssh</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -L59000:localhost:5901</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -C</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -N</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> lab</span></span></code></pre></div><p>4.2 Download and install tigervnc on your local computer.</p><details class="details custom-block"><summary>Windows</summary><p><a href="https://sourceforge.net/projects/tigervnc/" target="_blank" rel="noreferrer">Download TigerVNC</a> on sourceforge.net (Click <strong><code>Download</code></strong>).</p></details><details class="details custom-block"><summary>OS X and MacOS</summary><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">brew</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --cask</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> tigervnc-viewer</span></span></code></pre></div></details><details class="details custom-block"><summary>Ubuntu</summary><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> update</span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">sudo</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> apt</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> tigervnc-viewer</span></span></code></pre></div></details><p>4.3 Connect your VNC client using the following address (see below).</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">127.0.0.1:59000</span></span></code></pre></div><h2 id="useful-commands-for-turbo-vnc" tabindex="-1">Useful commands for Turbo VNC <a class="header-anchor" href="#useful-commands-for-turbo-vnc" aria-label="Permalink to &quot;Useful commands for Turbo VNC&quot;">​</a></h2><p>TurboVNC commands are run inside your lab machine.</p><h3 id="list-running-sessions" tabindex="-1">List running sessions <a class="header-anchor" href="#list-running-sessions" aria-label="Permalink to &quot;List running sessions&quot;">​</a></h3><p>If you have one or more existing sessions running and you want to list them, you can use <code>-list</code> parameter:</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/opt/TurboVNC/bin/vncserver</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -list</span></span></code></pre></div><h3 id="stop-session" tabindex="-1">Stop session <a class="header-anchor" href="#stop-session" aria-label="Permalink to &quot;Stop session&quot;">​</a></h3><p>You can stop your session by using <code>-kill</code> parameter and <code>:n</code> being a number of session (e.g. <code>:1</code>):</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">/opt/TurboVNC/bin/vncserver</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -kill</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> :n</span></span></code></pre></div><h2 id="troubleshooting" tabindex="-1">Troubleshooting <a class="header-anchor" href="#troubleshooting" aria-label="Permalink to &quot;Troubleshooting&quot;">​</a></h2><p>.</p>`,37)])])}const k=a(n,[["render",l]]);export{u as __pageData,k as default};
