import{_ as a,c as i,o as t,aM as n}from"./chunks/framework.DAyGILei.js";const u=JSON.parse('{"title":"Accountability","description":"This page gives an overview of the accountabilities for HUNT Cloud.","frontmatter":{"title":"Accountability","category":"HUNT Cloud","permalink":"/govern-science/accountability","description":"This page gives an overview of the accountabilities for HUNT Cloud."},"headers":[],"relativePath":"govern-science/accountability.md","filePath":"govern-science/accountability.md"}'),r={name:"govern-science/accountability.md"};function o(l,e,c,s,h,d){return t(),i("div",null,[...e[0]||(e[0]=[n('<h1 id="accountability" tabindex="-1">Accountability <a class="header-anchor" href="#accountability" aria-label="Permalink to &quot;Accountability&quot;">​</a></h1><p><strong>Accountability for HUNT Cloud&#39;s activities are as follows.</strong></p><nav class="table-of-contents"><ul><li><a href="#ownership">Ownership</a></li><li><a href="#governance">Governance</a></li><li><a href="#operations">Operations</a></li><li><a href="#scientific-development">Scientific development</a></li><li><a href="#affiliation">Affiliation</a></li></ul></nav><h3 id="ownership" tabindex="-1">Ownership <a class="header-anchor" href="#ownership" aria-label="Permalink to &quot;Ownership&quot;">​</a></h3><p>HUNT Cloud is owned by the <a href="https://www.ntnu.edu" target="_blank" rel="noreferrer">Norwegian University of Science and Technology (NTNU)</a>, Trondheim, Norway. Enterprise number 974 767 880.</p><h3 id="governance" tabindex="-1">Governance <a class="header-anchor" href="#governance" aria-label="Permalink to &quot;Governance&quot;">​</a></h3><p>The Head of Department at the Department of Public Health and Nursing at the Faculty of Medicine and Health Sciences, NTNU, <a href="https://www.ntnu.edu/ism/contact" target="_blank" rel="noreferrer">Solveig Kirsti Grudt</a> is responsible for our contractual obligations.</p><h3 id="operations" tabindex="-1">Operations <a class="header-anchor" href="#operations" aria-label="Permalink to &quot;Operations&quot;">​</a></h3><p>The Head of HUNT Cloud, <a href="https://www.ntnu.edu/employees/oddgeir.lingaas.holmen" target="_blank" rel="noreferrer">Oddgeir Lingaas Holmen</a>, holds the direct responsibility for our day-to-day operations, including management of information security and privacy, and the overall scientific development of products and services.</p><h3 id="scientific-development" tabindex="-1">Scientific development <a class="header-anchor" href="#scientific-development" aria-label="Permalink to &quot;Scientific development&quot;">​</a></h3><p><a href="/govern-science/tingweek/lagtun">Lagtun</a> holds an indirect responsibility for the scientific development of HUNT Cloud as member representatives express recommendations for the scientific governance directly to the Head of HUNT Research Centre in the form of resolutions.</p><h3 id="affiliation" tabindex="-1">Affiliation <a class="header-anchor" href="#affiliation" aria-label="Permalink to &quot;Affiliation&quot;">​</a></h3><p>HUNT Cloud is affiliated with the <a href="https://www.ntnu.edu/ism" target="_blank" rel="noreferrer">Department of Public Health and Nursing</a>, <a href="https://www.ntnu.edu/mh" target="_blank" rel="noreferrer">Faculty of Medicine and Health Sciences</a>, <a href="https://www.ntnu.edu" target="_blank" rel="noreferrer">Norwegian University of Science and Technology (NTNU)</a>, Trondheim, Norway.</p>',13)])])}const p=a(r,[["render",o]]);export{u as __pageData,p as default};
