import{_ as a,c as t,o as r,aM as i}from"./chunks/framework.DAyGILei.js";const m=JSON.parse('{"title":"Compliance","description":"This page contains information on the compliance program in HUNT Cloud.","frontmatter":{"title":"Compliance","category":"Govern science","permalink":"/govern-science/compliance/","description":"This page contains information on the compliance program in HUNT Cloud."},"headers":[],"relativePath":"govern-science/compliance/index.md","filePath":"govern-science/compliance/index.md"}'),o={name:"govern-science/compliance/index.md"};function n(s,e,c,l,d,u){return r(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="compliance-program" tabindex="-1">Compliance program <a class="header-anchor" href="#compliance-program" aria-label="Permalink to &quot;Compliance program&quot;">​</a></h1><p><strong>We believe that trust is the single most valuable asset in data-driven science. Our compliance program aims to ensure a trustworthy frame for which you can build your science.</strong></p><nav class="table-of-contents"><ul><li><a href="#certificate-audits">Certificate audits</a></li><li><a href="#partner-audits">Partner audits</a></li><li><a href="#internal-audits">Internal audits</a></li><li><a href="#subcontractors">Subcontractors</a></li></ul></nav><h2 id="certificate-audits" tabindex="-1">Certificate audits <a class="header-anchor" href="#certificate-audits" aria-label="Permalink to &quot;Certificate audits&quot;">​</a></h2><p><strong>We are proud to work with data controllers that expect strict independent compliance verifications.</strong></p><p>HUNT Cloud comply with two international standards, IEC/ISO 27001 for information security and privacy and IEC/ISO 9001 for quality management.</p><p><a href="./certificates">Click here to see our certificates</a>.</p><p>Independent auditors verify our certifications each year in two separate audits. Audit reports are made available to data controllers on request.</p><p>Identification of non-conformities will be communicated to data controllers and reflected in our Statement of Applicability (SoA).</p><h2 id="partner-audits" tabindex="-1">Partner audits <a class="header-anchor" href="#partner-audits" aria-label="Permalink to &quot;Partner audits&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>We welcome audits from data controllers and research leaders to ensure compliance with your expectations.</p></div><p>Representatives from data controllers are also welcome to participate in our internal audits for educational purposes. <a href="/contact">Contact us</a> to schedule a partner audits.</p><h2 id="internal-audits" tabindex="-1">Internal audits <a class="header-anchor" href="#internal-audits" aria-label="Permalink to &quot;Internal audits&quot;">​</a></h2><p>Our internal audit program aims to review the various parts of our activities within a three year certificate cycles. The program includes a systematic set of vertical audits for in-depth views of individual parts of our management systems. Results from these audits are available for data controllers on request.</p><h2 id="subcontractors" tabindex="-1">Subcontractors <a class="header-anchor" href="#subcontractors" aria-label="Permalink to &quot;Subcontractors&quot;">​</a></h2><p>HUNT Cloud delivers services that include the use of subcontractors. The subcontractors are subject to laws, regulations and terms no less protective than the terms HUNT Cloud is subject to under data processor agreements with our data controllers.</p><p><a href="./subcontractors">Click here to see our subcontractors</a>.</p>',17)])])}const h=a(o,[["render",n]]);export{m as __pageData,h as default};
