import{_ as i,c as n,o as t,aM as o}from"./chunks/framework.DAyGILei.js";const p=JSON.parse('{"title":"Introduction","description":"This page gives an introduction to the HUNT Cloud governance.","frontmatter":{"title":"Introduction","category":"Govern science","permalink":"/govern-science/introduction","description":"This page gives an introduction to the HUNT Cloud governance."},"headers":[],"relativePath":"govern-science/introduction.md","filePath":"govern-science/introduction.md"}'),r={name:"govern-science/introduction.md"};function a(c,e,s,l,d,u){return t(),n("div",null,[...e[0]||(e[0]=[o('<h1 id="govern-science-in-hunt-cloud" tabindex="-1">Govern science in HUNT Cloud <a class="header-anchor" href="#govern-science-in-hunt-cloud" aria-label="Permalink to &quot;Govern science in HUNT Cloud&quot;">​</a></h1><p><strong>Handling of large data values requires trustworthy and predictable structures.</strong></p><div class="tip custom-block"><p class="custom-block-title">In short</p><p>Our scientific development is governed by multilateral Tings, while the privacy and security policies of data values is governed directly by us in symphony with instructions from our data controllers.</p></div><h3 id="scientific-development" tabindex="-1">Scientific development <a class="header-anchor" href="#scientific-development" aria-label="Permalink to &quot;Scientific development&quot;">​</a></h3><ul><li><a href="/govern-science/tingweek/">Tingweek</a>. A set of layered Tings meet three times per year to provide recommendations on the scientific development in HUNT Cloud.</li><li><a href="/govern-science/tingweek/lagtun">Lagtun</a>. Lagtun is the Ting that govern the scientific development of our environments.</li><li><a href="/govern-science/tingweek/resolutions">Resolutions</a>. Lagtun expressed its recommendation for the scientific development as resolutions.</li></ul><h3 id="privacy-and-security" tabindex="-1">Privacy and security <a class="header-anchor" href="#privacy-and-security" aria-label="Permalink to &quot;Privacy and security&quot;">​</a></h3><ul><li><a href="/govern-science/risk-management/#clarification-of-responsibilities">Responsibilities</a>. An overview of the responsibilities for our governance.</li><li><a href="/govern-science/policies/">Policies</a>. The security and privacy policies that govern the protection of data values in HUNT Cloud.</li><li><a href="/govern-science/compliance/certificates">Certificates</a>. Third-party verification of our quality and security policies.</li><li><a href="/govern-science/compliance/subcontractors">Subcontractors</a>. A list of subcontractors for HUNT Cloud.</li></ul>',7)])])}const h=i(r,[["render",a]]);export{p as __pageData,h as default};
