import{_ as a,c as t,o,aM as i}from"./chunks/framework.DAyGILei.js";const u=JSON.parse('{"title":"Resources","description":"This page holds information on data safety levels in HUNT Cloud.","frontmatter":{"title":"Resources","category":"HUNT Cloud","permalink":"/riskmanagement/data-safety-levels","outline":1,"description":"This page holds information on data safety levels in HUNT Cloud."},"headers":[],"relativePath":"govern-science/risk-management/data-safety-levels.md","filePath":"govern-science/risk-management/data-safety-levels.md"}'),n={name:"govern-science/risk-management/data-safety-levels.md"};function s(r,e,l,d,h,c){return o(),t("div",null,[...e[0]||(e[0]=[i('<h1 id="data-safety-levels" tabindex="-1">Data safety levels <a class="header-anchor" href="#data-safety-levels" aria-label="Permalink to &quot;Data safety levels&quot;">​</a></h1><p><strong>This is a holding page for our data safety levels (DSL) pilot program. DSL is our approach to a set of layered information security and privacy controls to allow for more science to be done among our scientists.</strong></p><nav class="table-of-contents"><ul><li><a href="#why">Why?</a></li><li><a href="#what">What?</a></li><li><a href="#how">How?</a></li><li><a href="#get-involved">Get involved</a></li></ul></nav><div class="tip custom-block"><p class="custom-block-title">Note</p><p>The DSL program will be discussed (and this page expanded) during the upcoming <a href="/govern-science/tingweek/">Tingweek #7</a> in October 2022.</p></div><h3 id="why" tabindex="-1">Why? <a class="header-anchor" href="#why" aria-label="Permalink to &quot;Why?&quot;">​</a></h3><p>The goal is to allow our scientists the opportunity to work with even more data types and even larger data sets in even larger collaborations.</p><p>To allow for such flexibility we see a need to standardize the possibilities to adjust our security configurations and audit opportunities.</p><h3 id="what" tabindex="-1">What? <a class="header-anchor" href="#what" aria-label="Permalink to &quot;What?&quot;">​</a></h3><p>Our DSL approach is inspired by the <a href="https://en.wikipedia.org/wiki/Biosafety_level" target="_blank" rel="noreferrer">biosafety levels</a> for scientific lab work.</p><p>We aim to evolve a set of predictable information security and privacy controls that range from the lowest data safety level 2 (DSL-2) in HUNT Cloud to the highest at level 4 (DSL-4).</p><h3 id="how" tabindex="-1">How? <a class="header-anchor" href="#how" aria-label="Permalink to &quot;How?&quot;">​</a></h3><p>We plan to discuss the implementation plans and further details on the control sets during <a href="/govern-science/tingweek/">Tingweek #7</a> in October 2022.</p><p>Default lab configurations are of 1 August 2022 defined to be on DSL-2.</p><p>We are currently piloting DSL-3 measures in co-operation with selected data spaces and St Olavs Hospital. Our goal is that such environments may be available for labs in 2023.</p><h3 id="get-involved" tabindex="-1">Get involved <a class="header-anchor" href="#get-involved" aria-label="Permalink to &quot;Get involved&quot;">​</a></h3><p><a href="/contact">Contact us</a> is you would like to contribute to the development of the DSL program, or if you have data that you think may be in need of additional protection and suited for protection under DSL-3.</p>',16)])])}const f=a(n,[["render",s]]);export{u as __pageData,f as default};
